import { useState, useEffect } from "react";
import { useDispatch } from "react-redux";
import { registerModal, popModal } from "../../redux/modalStackSlice";
import { setUserInfo } from "../../redux/authSlice";
import { login } from "../../api/auth";
import FocusLock from "react-focus-lock";

export default function SignupModal() {
  const [modalShow, setModalShow] = useState(false);
  const [modalInfo, setModalInfo] = useState({ fade: false, z: 0 });
  // show : 모달 띄울 것인가, fade : 블러 처리 여부, z : z-index

  const dispatch = useDispatch();

  // 입력값 상태 관리
  const [email, setEmail] = useState("");
  const [password, setPassword] = useState("");
  const [passwordConfirm, setPasswordConfirm] = useState("");
  const [nickname, setNickname] = useState("");
  const [showPassword, setShowPassword] = useState(false);

  async function handleSignup() {
    // 비밀번호 확인 검사
    if (password !== passwordConfirm) {
      alert("비밀번호와 비밀번호 확인이 일치하지 않습니다.");
      return;
    }

    let res = await login(email, password);
    if (res !== null) {
      dispatch(
        setUserInfo({
          nickname: res.nickname,
          username: res.username,
          accessToken: res.accessToken,
        })
      );
      dispatch(popModal());
    }
  }

  function togglePasswordVisibility() {
    setShowPassword((prev) => !prev);
  }

  // 모달 초기화 함수: 상태 초기화만 하면 됨
  function modalClear() {
    setEmail("");
    setPassword("");
    setPasswordConfirm("");
    setNickname("");
    setShowPassword(false);
  }

  useEffect(() => {
    dispatch(
      registerModal({
        modalName: "signup",
        modalClear,
        setModalShow,
        setModalInfo,
      })
    );
  }, [dispatch]);

  return (
    <div id="signup-modal">
      {modalShow && (
        <div
          className={"fixed inset-0 flex justify-center items-center z-" + modalInfo.z}
          onClick={() => {
            dispatch(popModal());
          }}
        >
          <div
            className={
              "bg-white relative rounded-lg p-6 w-96 max-h-[80vh] overflow-y-auto" +
              (modalInfo.fade
                ? " fixed inset-0 bg-black bg-opacity-30 backdrop-blur-sm"
                : "")
            }
            onClick={(e) => e.stopPropagation()}
          >
            <button
              className="absolute top-2 right-2 px-2 py-0.8 bg-gray-100 rounded hover:bg-gray-200"
              onClick={() => {
                dispatch(popModal());
              }}
            >
              ×
            </button>

            <h2 className="text-xl font-semibold mb-4">회원가입</h2>
            <FocusLock>
              <form
                onSubmit={async (e) => {
                  e.preventDefault();
                  await handleSignup();
                }}
                className="flex flex-col space-y-4 items-center"
              >
                <div className="flex items-center gap-x-2 w-full">
                  <span className="mr-2">👤</span>
                  <input
                    type="text"
                    id="signup-nickname"
                    placeholder="닉네임"
                    autoComplete="off"
                    className="flex-1 min-w-0 px-4 py-2 border border-gray-300 rounded-md focus:outline-none focus:ring-2 focus:ring-indigo-300"
                    required
                    value={nickname}
                    onChange={(e) => setNickname(e.target.value)}
                  />
                </div>

                <div className="flex items-center gap-x-2 w-full">
                  <span className="mr-2">📧</span>
                  <input
                    type="email"
                    id="signup-email"
                    placeholder="이메일"
                    autoComplete="new-email"
                    className="flex-1 min-w-0 px-4 py-2 border border-gray-300 rounded-md focus:outline-none focus:ring-2 focus:ring-indigo-300"
                    required
                    value={email}
                    onChange={(e) => setEmail(e.target.value)}
                  />
                </div>

                <div className="flex items-center gap-x-2 w-full relative">
                  <span className="mr-2">🔒</span>
                  <input
                    type={showPassword ? "text" : "password"}
                    id="signup-password"
                    placeholder="비밀번호"
                    autoComplete="new-password"
                    className="w-full px-4 py-2 pr-10 border border-gray-300 rounded-md focus:outline-none focus:ring-2 focus:ring-indigo-300"
                    required
                    value={password}
                    onChange={(e) => setPassword(e.target.value)}
                  />
                  <button
                    type="button"
                    onClick={togglePasswordVisibility}
                    className="absolute right-2 top-1/2 transform -translate-y-1/2 text-gray-500 hover:text-gray-700"
                    tabIndex={-1} // 포커스 방지
                  >
                    {showPassword ? "🕶️" : "👀"}
                  </button>
                </div>

                <div className="flex items-center gap-x-2 w-full">
                  <span className="mr-2">🔏</span>
                  <input
                    type={showPassword ? "text" : "password"}
                    id="signup-password-confirm"
                    placeholder="비밀번호 확인"
                    autoComplete="new-password"
                    className="flex-1 min-w-0 px-4 py-2 border border-gray-300 rounded-md focus:outline-none focus:ring-2 focus:ring-indigo-300"
                    required
                    value={passwordConfirm}
                    onChange={(e) => setPasswordConfirm(e.target.value)}
                  />
                </div>

                <button
                  type="submit"
                  className="w-full bg-indigo-500 text-white px-4 py-2 rounded hover:bg-indigo-700"
                >
                  회원가입
                </button>
              </form>
            </FocusLock>
          </div>
        </div>
      )}
    </div>
  );
}
