import React, { useState, useEffect, useRef, useCallback } from 'react';
import PostCard from './PostCard';
import { useDispatch, useSelector } from 'react-redux';
import { showModal } from '../redux/modalStackSlice';

const API_URL = 'http://localhost:8080/dumbo-backend/post';
const MAX_POSTS = 30;

export default function ArticleTest() {
  const [posts, setPosts] = useState([]);
  const [topCursor, setTopCursor] = useState(null);     // reverse=true용
  const [bottomCursor, setBottomCursor] = useState(null); // reverse=false용
  const [hasMoreUp, setHasMoreUp] = useState(true);
  const [hasMoreDown, setHasMoreDown] = useState(true);
  const [isLoading, setIsLoading] = useState(false);

  const topSentinelRef = useRef(null);
  const bottomSentinelRef = useRef(null);

  const dispatch = useDispatch();

  const fetchPosts = useCallback(async (reverse = false) => {
    if (isLoading || (!hasMoreDown && !reverse) || (!hasMoreUp && reverse)) return;

    setIsLoading(true);

    try {
      const params = new URLSearchParams();
      const cursor = reverse ? topCursor : bottomCursor;

      if (cursor?.createdAt) params.append('createdAtCursor', cursor.createdAt);
      if (cursor?.postId) params.append('postIdCursor', cursor.postId);
      if (reverse) params.append('reverse', 'true');

      const res = await fetch(`${API_URL}?${params.toString()}`);
      if (!res.ok) throw new Error('Failed to fetch posts');

      const json = await res.json();
      const newPosts = json.data || [];

      setPosts(prev => {
        const combined = reverse
          ? [...newPosts, ...prev]
          : [...prev, ...newPosts];

        // 최대 30개 유지
        return reverse ? combined.slice(0, MAX_POSTS) : combined.slice(-MAX_POSTS);
      });

      if (newPosts.length > 0) {
        const first = newPosts[0];
        const last = newPosts[newPosts.length - 1];

        if (reverse) setTopCursor({ createdAt: first.created_at, postId: first.post_id });
        else setBottomCursor({ createdAt: last.created_at, postId: last.post_id });
      }

      if (reverse) setHasMoreUp(json.hasMore);
      else setHasMoreDown(json.hasMore);

    } catch (error) {
      console.error('게시글 로딩 오류:', error);
    } finally {
      setIsLoading(false);
    }
  }, [topCursor, bottomCursor, hasMoreUp, hasMoreDown, isLoading]);

  useEffect(() => {
    fetchPosts(false); // 초기 로드 (아래 방향)
  }, []);

  // IntersectionObserver - 위
  useEffect(() => {
    if (!topSentinelRef.current) return;
    const observer = new IntersectionObserver(
      ([entry]) => {
        if (entry.isIntersecting && hasMoreUp && !isLoading) {
          fetchPosts(true); // reverse: true -> 위로
        }
      },
      { rootMargin: '200px' }
    );
    observer.observe(topSentinelRef.current);
    return () => observer.disconnect();
  }, [fetchPosts, hasMoreUp, isLoading]);

  // IntersectionObserver - 아래
  useEffect(() => {
    if (!bottomSentinelRef.current) return;
    const observer = new IntersectionObserver(
      ([entry]) => {
        if (entry.isIntersecting && hasMoreDown && !isLoading) {
          fetchPosts(false); // reverse: false -> 아래로
        }
      },
      { rootMargin: '200px' }
    );
    observer.observe(bottomSentinelRef.current);
    return () => observer.disconnect();
  }, [fetchPosts, hasMoreDown, isLoading]);

  const modalStack = useSelector(state => state.modal.modalStack);
  useEffect(() => {
    document.body.style.overflow = modalStack.length > 0 ? 'hidden' : '';
    return () => { document.body.style.overflow = ''; };
  }, [modalStack.length]);

  const isLoggedIn = useSelector(state => state.auth.accessToken);

  return (
    <div className="max-w-3xl mx-auto mt-10 px-4">
      <div ref={topSentinelRef} style={{ height: 1 }} />

      {posts.map(post => (
        <PostCard key={post.post_id} post={post} />
      ))}

      <div ref={bottomSentinelRef} style={{ height: 1 }} />

      {isLoading && (
        <div className="flex justify-center py-6">
          <div className="animate-spin rounded-full h-8 w-8 border-t-2 border-b-2 border-indigo-500" />
        </div>
      )}

      {!hasMoreUp && !hasMoreDown && (
        <div className="text-center text-gray-400 py-4">
          모든 게시글을 불러왔습니다.
        </div>
      )}

      {isLoggedIn && (
        <button
          onClick={() => dispatch(showModal('draft'))}
          className="fixed left-8 bottom-8 px-4 py-4 bg-indigo-500 text-2xl rounded-full shadow-lg cursor-pointer hover:bg-indigo-700"
        >
          🦦
        </button>
      )}
    </div>
  );
}
