import React, { useState } from 'react';
import { useSelector } from "react-redux";
import PostRequestWithAccessToken from '../api/axios/requestWithAccessToken';

export default function CommentsSection({
  comments,
  onToggle,
  postId,
  isLoading,
  replyTo,
  setReplyTo, // ✅ 부모에서 전달받음
}) {
  const [newCommentText, setNewCommentText] = useState('');
  const accessToken = useSelector(state => state.auth.accessToken);

  const handleWriteComment = async () => {
    if (!newCommentText.trim()) return;
    try {
      const payload = {
        content: newCommentText,
      };
      if (replyTo) payload.reply_to = replyTo.commentId;

      const res = await PostRequestWithAccessToken(accessToken, "/comment/" + postId, payload);
      console.log(res.data);
      setNewCommentText('');
      setReplyTo(null); // 작성 후 답글 상태 초기화
      // 댓글 목록 다시 불러오기 필요 시 이곳에 처리
    } catch (error) {
      console.error("댓글 작성 실패", error);
    }
  };

  return (
    <div
      className="p-4 border rounded bg-gray-50 overflow-y-auto max-h-96 flex flex-col"
      style={{ height: '100%' }}
    >
      <h3 className="text-sm font-semibold mb-2">댓글</h3>

      <ul className="text-sm text-gray-700 space-y-6 flex-grow overflow-y-auto mb-4 pr-2">
        {comments.map((comment) => (
          <CommentItem
            key={comment.comment_id}
            comment={comment}
            onToggle={onToggle}
            onReply={() => setReplyTo({ commentId: comment.comment_id, authorNickname: comment.author_nickname, commentContent: comment.content })}
          />
        ))}
      </ul>

      <div className="pt-3 border-t border-gray-300">
        {replyTo && (
          <div className="mb-2 text-sm text-gray-600 flex items-center justify-between">
            <span>
              답글 작성 : <strong>{replyTo.authorNickname}</strong> {replyTo.commentContent}
            </span>
            <button
              onClick={() => setReplyTo(null)}
              className="text-xs text-red-500 hover:underline"
            >
              ✕ 취소
            </button>
          </div>
        )}
        <form
          onSubmit={(e) => {
            e.preventDefault();
            handleWriteComment();
          }}
          className="flex items-center"
        >
          <input
            type="text"
            value={newCommentText}
            onChange={(e) => setNewCommentText(e.target.value)}
            placeholder={replyTo ? "답글을 입력하세요" : "댓글을 입력하세요"}
            className="flex-grow border rounded-l px-3 py-2 text-sm focus:outline-none focus:ring-2 focus:ring-blue-400"
          />
          <button
            type="submit"
            className={`bg-blue-500 hover:bg-blue-600 text-white rounded-r px-4 py-2 text-sm ${
              newCommentText.trim() ? 'cursor-pointer' : 'cursor-not-allowed opacity-50'
            }`}
            disabled={!newCommentText.trim()}
          >
            쓰기
          </button>
        </form>
      </div>
    </div>
  );
}

function CommentItem({ comment, onToggle, onReply }) {
  const [showReplies, setShowReplies] = useState(false);

  const {
    comment_id,
    author_nickname,
    content,
    created_at,
    replies = [],
    reply_count = 0,
  } = comment;

  function handleToggle() {
    setShowReplies((prev) => {
      const newState = !prev;
      setTimeout(() => {
        if (onToggle) onToggle(comment_id, newState);
      }, 0);
      return newState;
    });
  }

  return (
    <li>
      <div className="flex items-center space-x-2 mb-1 text-gray-800 font-semibold text-base">
        <span>{author_nickname}</span>
        <span className="text-xs text-gray-400">
          {new Date(created_at * 1000).toLocaleString()}
        </span>
      </div>

      {/* ✅ 댓글 본문을 클릭하면 답글쓰기 상태로 진입 */}
      <div
        className="mb-2 text-gray-700 cursor-pointer hover:bg-gray-100 p-1 rounded"
        onClick={onReply}
      >
        {content}
      </div>

      {/* 답글 더보기 버튼은 그대로 유지 */}
      {reply_count > 0 && (
        <button
          onClick={handleToggle}
          className="text-sm text-blue-500 hover:underline"
          type="button"
        >
          {showReplies ? '답글 숨기기' : `+ 답글 ${reply_count}개 보기`}
        </button>
      )}

      {showReplies && replies.length > 0 && (
        <ul className="ml-6 mt-3 space-y-4 border-l-2 border-gray-300 pl-4">
          {replies.map((reply) => (
            <li key={reply.id}>
              <div className="flex items-center space-x-2 text-gray-800 font-semibold text-base">
                <span>{reply.author}</span>
                <span className="text-xs text-gray-400">
                  {new Date(reply.createdAt * 1000).toLocaleString()}
                </span>
              </div>
              <div className="text-gray-700">{reply.content}</div>
            </li>
          ))}
        </ul>
      )}
    </li>
  );
}
