import React from 'react';
import { FixedSizeList as List } from 'react-window';
import PostCard from './PostCard'; // 경로 맞게 수정하세요

const bigComments = [
  {
    id: "c1",
    author: "user23",
    content: "댓글 내용 547",
    createdAt: "2023-07-24T03:54:01.112Z",
    replies: [
      {
        id: "r1_1",
        author: "replyUser15",
        content: "답글 내용 196",
        createdAt: "2024-01-05T19:10:14.232Z",
      },
      {
        id: "r1_2",
        author: "replyUser84",
        content: "답글 내용 438",
        createdAt: "2025-02-28T11:39:52.821Z",
      },
    ],
  },
  {
    id: "c2",
    author: "user41",
    content: "댓글 내용 803",
    createdAt: "2023-08-12T12:22:44.639Z",
    replies: [],
  },
  {
    id: "c3",
    author: "user99",
    content: "댓글 내용 142",
    createdAt: "2025-04-16T07:01:12.761Z",
    replies: [
      {
        id: "r3_1",
        author: "replyUser56",
        content: "답글 내용 814",
        createdAt: "2025-04-23T17:25:43.372Z",
      },
    ],
  },
  // 더 추가 가능
];

const samplePost = {
  nickname: '홍길동',
  title: '첫 게시글입니다!',
  createdAt: '2025-06-13T10:00:00Z',
  updatedAt: '2025-06-13T12:00:00Z',
  contentHtml: `<p>이건 정말 긴 게시글의 예시입니다. <strong>HTML 형식</strong>도 포함돼요.<br>줄바꿈도 됩니다.</p>
                <p>이미지도 가능하고, 다양한 포맷이 적용될 수 있습니다. 여기에 아주 많은 텍스트가 있다고 가정합시다...</p>
                <p>이건 정말 긴 게시글의 예시입니다. <strong>HTML 형식</strong>도 포함돼요.<br>줄바꿈도 됩니다.</p>
                <p>이미지도 가능하고, 다양한 포맷이 적용될 수 있습니다. 여기에 아주 많은 텍스트가 있다고 가정합시다...</p>
                <p>이건 정말 긴 게시글의 예시입니다. <strong>HTML 형식</strong>도 포함돼요.<br>줄바꿈도 됩니다.</p>
                <p>이미지도 가능하고, 다양한 포맷이 적용될 수 있습니다. 여기에 아주 많은 텍스트가 있다고 가정</p>`,
  thumbnailImage: 'https://via.placeholder.com/600x300',
  comments: bigComments,
};

function CommentRow({ index, style, data }) {
  const comment = data[index];
  return (
    <div style={style} key={comment.id} className="border-b p-3">
      <p className="font-semibold">{comment.author} <span className="text-sm text-gray-500">- {new Date(comment.createdAt).toLocaleString()}</span></p>
      <p className="mt-1">{comment.content}</p>
      {comment.replies.length > 0 && (
        <div className="ml-6 mt-2 border-l-2 border-gray-300 pl-4">
          {comment.replies.map(reply => (
            <div key={reply.id} className="mb-2">
              <p className="font-semibold">{reply.author} <span className="text-sm text-gray-500">- {new Date(reply.createdAt).toLocaleString()}</span></p>
              <p>{reply.content}</p>
            </div>
          ))}
        </div>
      )}
    </div>
  );
}

export default function ArticleTest() {
  return (
    <div className="max-w-xl mx-auto mt-10">
      {/* 게시글 본문만 렌더링 */}
      <PostCard post={{ ...samplePost, comments: [] }} />

      {/* 댓글 리스트 가상 스크롤 */}
      <div className="mt-6 border rounded" style={{ height: 400 }}>
        <List
          height={400}
          itemCount={samplePost.comments.length}
          itemSize={120}  // 댓글 아이템 높이 (답글 있을 때 좀 더 여유있게)
          width="100%"
          itemData={samplePost.comments}
        >
          {CommentRow}
        </List>
      </div>
    </div>
  );
}
