// App.js
import Header from './components/Header';
import PostTemp from './components/PostTemp';
import Modals from './components/modals/Modals';
import { useEffect } from 'react';
import { requestTokenPair } from './api/auth';
import { useDispatch } from 'react-redux';
import { setUserInfo } from './redux/authSlice';

function App() 
{
  const dispatch = useDispatch();

  useEffect(() =>
  { async() => 
    {
      // 리프레시 쿠키가 있다면 reissue 로 로그인 유지하는 루틴
      let res = await requestTokenPair();
      if (res !== null)
      {
        dispatch(setUserInfo({ 'nickname' : res.nickname, 'userId' : res.userId, 'accessToken' : res.accessToken }));
      }
    }
  }, []);

  return (
    <div>
      <Modals />
      <Header />
      <PostTemp />
    </div>
  );
}

export default App;


