import React, { useState, useEffect, useRef, useCallback } from 'react';
import { FixedSizeList as List } from 'react-window';
import PostCard from './PostCard'; // 경로 맞게 수정

// 예시 댓글 데이터
const bigComments = [
  {
    id: "c1",
    author: "user23",
    content: "댓글 내용 547",
    createdAt: "2023-07-24T03:54:01.112Z",
    replies: [
      { id: "r1_1", author: "replyUser15", content: "답글 내용 196", createdAt: "2024-01-05T19:10:14.232Z" },
      { id: "r1_2", author: "replyUser84", content: "답글 내용 438", createdAt: "2025-02-28T11:39:52.821Z" },
    ],
  },
  {
    id: "c2",
    author: "user41",
    content: "댓글 내용 803",
    createdAt: "2023-08-12T12:22:44.639Z",
    replies: [],
  },
  {
    id: "c3",
    author: "user99",
    content: "댓글 내용 142",
    createdAt: "2025-04-16T07:01:12.761Z",
    replies: [
      { id: "r3_1", author: "replyUser56", content: "답글 내용 814", createdAt: "2025-04-23T17:25:43.372Z" },
    ],
  },
];

// 여러 게시글 데이터 예시 (20개)
const samplePosts = Array(20).fill(null).map((_, i) => ({
  id: `post_${i + 1}`,
  nickname: `홍길동_${i + 1}`,
  title: `게시글 제목 ${i + 1}`,
  createdAt: '2025-06-13T10:00:00Z',
  updatedAt: '2025-06-13T12:00:00Z',
  contentHtml: `<p>게시글 내용 예시 ${i + 1}. <strong>HTML 가능</strong>합니다.</p>`,
  thumbnailImage: 'https://via.placeholder.com/600x300',
  comments: bigComments,
}));

// 댓글 한 줄 렌더링 컴포넌트
function CommentRow({ index, style, data }) {
  const comment = data[index];
  return (
    <div style={style} key={comment.id} className="border-b p-3">
      <p className="font-semibold">
        {comment.author} <span className="text-sm text-gray-500">- {new Date(comment.createdAt).toLocaleString()}</span>
      </p>
      <p className="mt-1">{comment.content}</p>
      {comment.replies.length > 0 && (
        <div className="ml-6 mt-2 border-l-2 border-gray-300 pl-4">
          {comment.replies.map(reply => (
            <div key={reply.id} className="mb-2">
              <p className="font-semibold">
                {reply.author} <span className="text-sm text-gray-500">- {new Date(reply.createdAt).toLocaleString()}</span>
              </p>
              <p>{reply.content}</p>
            </div>
          ))}
        </div>
      )}
    </div>
  );
}

// 게시글 + 댓글 토글 포함 컴포넌트
function PostWithComments({ post }) {
  const [showComments, setShowComments] = useState(false);

  return (
    <div className="mb-12 border rounded p-4 shadow-sm">
      <PostCard post={{ ...post, comments: [] }} />

      <button
        onClick={() => setShowComments(prev => !prev)}
        className="mt-4 px-4 py-2 bg-blue-600 text-white rounded hover:bg-blue-700"
      >
        {showComments ? '댓글 숨기기' : `댓글 보기 (${post.comments.length})`}
      </button>

      {showComments && (
        <div className="mt-6 border rounded" style={{ height: 200 }}>
          <List
            height={200}
            itemCount={post.comments.length}
            itemSize={120}
            width="100%"
            itemData={post.comments}
          >
            {CommentRow}
          </List>
        </div>
      )}
    </div>
  );
}

// 무한 스크롤 피드 메인 컴포넌트
export default function InfiniteFeed() {
  const [posts, setPosts] = useState(samplePosts.slice(0, 5)); // 처음 5개 로드
  const loadMoreRef = useRef();

  // 더 불러오기 함수 (시뮬레이션)
  const loadMore = useCallback(() => {
    setPosts(prev => {
      const nextIndex = prev.length;
      if (nextIndex >= samplePosts.length) return prev; // 끝까지 로드했으면 추가 없음
      const nextPosts = samplePosts.slice(nextIndex, nextIndex + 5);
      return [...prev, ...nextPosts];
    });
  }, []);

  // IntersectionObserver로 스크롤 끝 감지
  useEffect(() => {
    const observer = new IntersectionObserver(entries => {
      if (entries[0].isIntersecting) {
        loadMore();
      }
    }, {
      rootMargin: '200px',
    });

    if (loadMoreRef.current) observer.observe(loadMoreRef.current);

    return () => {
      if (loadMoreRef.current) observer.unobserve(loadMoreRef.current);
    };
  }, [loadMore]);

  return (
    <div className="max-w-xl mx-auto mt-10 px-4">
      {posts.map(post => (
        <PostWithComments key={post.id} post={post} />
      ))}

      {/* 무한 스크롤 로딩 트리거용 빈 div */}
      <div ref={loadMoreRef} style={{ height: 1 }}></div>
    </div>
  );
}
