import React, { useState, useEffect, useRef } from 'react';
import { Card, CardContent } from './ui/card';
import CommentsSection from './CommentsSection';

// ÎÇ†Ïßú Ìè¨Îß∑ÌåÖ Ìï®Ïàò
function formatDateTime(timestamp) {
  if (!timestamp) return '';
  const date = new Date(timestamp);

  const yyyy = date.getFullYear();
  const mm = date.getMonth() + 1;
  const dd = date.getDate();

  let hh = date.getHours();
  const min = date.getMinutes().toString().padStart(2, '0');

  const ampm = hh < 12 ? 'Ïò§Ï†Ñ' : 'Ïò§ÌõÑ';
  hh = hh % 12;
  if (hh === 0) hh = 12;

  return `${yyyy}. ${mm}. ${dd}. ${ampm} ${hh}:${min}`;
}

// HTMLÏùÑ ÏïàÏ†ÑÌïòÍ≤å ÏûêÎ•¥Îêò <img> ÌÉúÍ∑∏Îäî Ï†úÍ±∞ÌïòÎäî Ìï®Ïàò
function truncateHtmlSafely(html, maxLength) {
  const div = document.createElement('div');
  div.innerHTML = html;

  // Ïù¥ÎØ∏ÏßÄ ÌÉúÍ∑∏ Ï†úÍ±∞
  const imgs = div.querySelectorAll('img');
  imgs.forEach((img) => img.remove());

  let currentLength = 0;

  function walk(node) {
    if (currentLength >= maxLength) return null;

    if (node.nodeType === Node.TEXT_NODE) {
      const remaining = maxLength - currentLength;
      const text = node.textContent.slice(0, remaining);
      currentLength += text.length;
      return document.createTextNode(text);
    }

    if (node.nodeType === Node.ELEMENT_NODE) {
      const clone = node.cloneNode(false);
      const children = Array.from(node.childNodes);

      for (let child of children) {
        const result = walk(child);
        if (result) clone.appendChild(result);
        if (currentLength >= maxLength) break;
      }

      return clone;
    }

    return null;
  }

  const result = walk(div);
  const container = document.createElement('div');
  if (result) container.appendChild(result);

  return container.innerHTML;
}

const COMMENTS_FIXED_HEIGHT = 384; // px

export default function PostCard({ post }) {
  const [expanded, setExpanded] = useState(false);
  const [showComments, setShowComments] = useState(false);
  const [repliesToggledMap, setRepliesToggledMap] = useState({});
  const [animatedHeight, setAnimatedHeight] = useState(0);
  const [needsTruncation, setNeedsTruncation] = useState(false);

  const previewRef = useRef(null);
  const fullContentRef = useRef(null);

  const {
    author_nickname: nickname,
    title,
    content_html: contentHtml,
    thumbnail_img_url: thumbnailImage,
    comments = [],
    created_at,
    updated_at,
  } = post;

  const createdAt = created_at ? created_at * 1000 : null;
  const updatedAt = updated_at !== null && updated_at !== undefined ? updated_at * 1000 : null;
  const isModified = updatedAt && updatedAt !== createdAt;

  // ÎØ∏Î¶¨Î≥¥Í∏∞Ïö© HTML: Ïù¥ÎØ∏ÏßÄ ÌÉúÍ∑∏ Ï†úÍ±∞
  const previewHtml = truncateHtmlSafely(contentHtml, 10000);

  // ÌÖçÏä§Ìä∏ ÎÇ¥Ïö©Îßå ÏµúÎåÄÍ∏∏Ïù¥ÍπåÏßÄ ÏûêÎ•¥Í∏∞ (ÎçîÎ≥¥Í∏∞ ÌëúÏãú ÏúÑÌï¥ÏÑú)
  // Ïó¨Í∏∞ÏÑú 300Ïûê Ï†úÌïúÏùÄ ÏòàÏãúÏûÖÎãàÎã§. ÌïÑÏöî Ïãú Ï°∞Ï†à Í∞ÄÎä•
  const previewTextMaxLength = 300;

  // ÏàúÏàò ÌÖçÏä§Ìä∏ Ï∂îÏ∂ú Ìó¨Ìçº Ìï®Ïàò (HTML ÌÉúÍ∑∏ Ï†úÍ±∞)
  function extractTextFromHtml(html) {
    const div = document.createElement('div');
    div.innerHTML = html;
    return div.textContent || div.innerText || '';
  }

  // ÎçîÎ≥¥Í∏∞Ïö© ÌÖçÏä§Ìä∏ (ÏûêÏó∞Ïä§ÎüΩÍ≤å ÎÅùÎÇ¥Í∏∞ ÏúÑÌïú ÎßàÏπ®Ìëú Îì± Í≥†Î†§ Í∞ÄÎä•)
  const fullText = extractTextFromHtml(previewHtml);
  const isTextTooLong = fullText.length > previewTextMaxLength;

  const truncatedText = isTextTooLong
    ? fullText.slice(0, previewTextMaxLength)
    : fullText;

  // Î≥∏Î¨∏ ÌÅ¥Î¶≠ Ïãú ÌéºÏπ® Ìö®Í≥º (Îã®, Ï†ëÍ∏∞ ÏÉÅÌÉúÏóêÏÑúÎßå)
  function onContentOrThumbnailClick() {
    if (!expanded && needsTruncation) {
      setExpanded(true);
    }
  }

  // ÎçîÎ≥¥Í∏∞ ÌÅ¥Î¶≠ (Ï†ëÍ∏∞ Î≤ÑÌäº Ï†úÏô∏Ìïú Î∂ÄÎ∂ÑÏóêÏÑú Î≥∏Î¨∏ ÌÅ¥Î¶≠ Ïãú ÌéºÏπ®Í≥º Î≥ÑÎèÑÎ°ú ÌÅ¥Î¶≠ Ïù¥Î≤§Ìä∏ Î∂ÑÎ¶¨)
  function onMoreClick(e) {
    e.stopPropagation();
    setExpanded(true);
  }

  // Ï†ëÍ∏∞ Î≤ÑÌäº ÌÅ¥Î¶≠ (Î≥∏Î¨∏ ÌÅ¥Î¶≠Í≥º Î∂ÑÎ¶¨)
  function onCollapseClick(e) {
    e.stopPropagation();
    setExpanded(false);
  }

  // ÎåìÍ∏Ä ÏòÅÏó≠ Ïï†ÎãàÎ©îÏù¥ÏÖò ÎÜíÏù¥ Ï°∞Ï†à
  useEffect(() => {
    if (showComments) {
      setAnimatedHeight(0);
      const timeoutId = setTimeout(() => {
        setAnimatedHeight(COMMENTS_FIXED_HEIGHT);
      }, 10);
      return () => clearTimeout(timeoutId);
    } else {
      setAnimatedHeight(0);
    }
  }, [showComments]);

  // Î≥∏Î¨∏ ÎØ∏Î¶¨Î≥¥Í∏∞ ÎÜíÏù¥ Ï≤¥ÌÅ¨ (Ïù¥ÎØ∏ÏßÄ Ï†úÏô∏)
  useEffect(() => {
    if (previewRef.current) {
      const height = previewRef.current.scrollHeight;
      setNeedsTruncation(height > 200);
    }
  }, [previewHtml]);

  // Ï†ÑÏ≤¥ Î≥∏Î¨∏(Ïù¥ÎØ∏ÏßÄ Ìè¨Ìï®) ÎÜíÏù¥ Ï≤¥ÌÅ¨ (Ïù¥ÎØ∏ÏßÄ Î°úÎî© ÏôÑÎ£å ÌõÑ)
  useEffect(() => {
    if (!fullContentRef.current) return;

    const imgs = fullContentRef.current.querySelectorAll('img');
    if (imgs.length === 0) {
      setNeedsTruncation(fullContentRef.current.scrollHeight > 200);
      return;
    }

    let loadedCount = 0;
    const onLoadOrError = () => {
      loadedCount += 1;
      if (loadedCount === imgs.length) {
        setNeedsTruncation(fullContentRef.current.scrollHeight > 200);
      }
    };

    imgs.forEach((img) => {
      if (img.complete) {
        onLoadOrError();
      } else {
        img.addEventListener('load', onLoadOrError);
        img.addEventListener('error', onLoadOrError);
      }
    });

    return () => {
      imgs.forEach((img) => {
        img.removeEventListener('load', onLoadOrError);
        img.removeEventListener('error', onLoadOrError);
      });
    };
  }, [contentHtml]);

