import React, { useState } from 'react';

export default function CommentsSection({ comments, onToggle }) {
  const [newCommentText, setNewCommentText] = useState('');

  return (
    <div className="p-4 border rounded bg-gray-50 overflow-y-auto max-h-96 flex flex-col">
      <h3 className="text-sm font-semibold mb-2">댓글</h3>
      <ul className="text-sm text-gray-700 space-y-6 flex-grow overflow-y-auto">
        {comments.map((comment) => (
          <CommentItem key={comment.id} comment={comment} onToggle={onToggle} />
        ))}
      </ul>

      <form
        onSubmit={(e) => {
          e.preventDefault();
          // 나중에 작성 기능 추가 예정
          setNewCommentText('');
        }}
        className="mt-4 border rounded-lg p-3 bg-white flex items-center"
      >
        <input
          type="text"
          placeholder="댓글을 입력하세요"
          value={newCommentText}
          onChange={(e) => setNewCommentText(e.target.value)}
          className="flex-grow border border-gray-300 rounded-full px-4 py-2 text-sm focus:outline-none focus:ring-2 focus:ring-blue-400"
        />
        <button
          type="submit"
          className="ml-2 bg-blue-500 hover:bg-blue-600 text-white rounded-full px-5 py-2 text-sm"
        >
          쓰기
        </button>
      </form>
    </div>
  );
}

function CommentItem({ comment, onToggle }) {
  const [showReplies, setShowReplies] = useState(false);
  const { author, content, createdAt, replies = [] } = comment;

  function handleToggle() {
    setShowReplies((prev) => {
      const newState = !prev;
      setTimeout(() => {
        if (onToggle) onToggle(comment.id, newState);
      }, 0);
      return newState;
    });
  }

  return (
    <li>
      <div className="flex items-center space-x-2 mb-1 text-gray-800 font-semibold text-base">
        <span>{author}</span>
        <span className="text-xs text-gray-400">{new Date(createdAt).toLocaleString()}</span>
      </div>
      <div className="mb-2 text-gray-700">{content}</div>

      {replies.length > 0 && (
        <button
          onClick={handleToggle}
          className="text-sm text-blue-500 hover:underline"
          type="button"
        >
          {showReplies ? '답글 숨기기' : `+ 답글 ${replies.length}개 보기`}
        </button>
      )}

      {showReplies && replies.length > 0 && (
        <ul className="ml-6 mt-3 space-y-4 border-l-2 border-gray-300 pl-4">
          {replies.map((reply) => (
            <li key={reply.id}>
              <div className="flex items-center space-x-2 text-gray-800 font-semibold text-base">
                <span>{reply.author}</span>
                <span className="text-xs text-gray-400">{new Date(reply.createdAt).toLocaleString()}</span>
              </div>
              <div className="text-gray-700">{reply.content}</div>
            </li>
          ))}
        </ul>
      )}
    </li>
  );
}
