import React, { useState } from "react";

export default function LoginModal({ isOpen, onClose }) {
  // 상태: 유저명, 비밀번호, 체크박스
  const [username, setUsername] = useState("");
  const [password, setPassword] = useState("");
  const [rememberUser, setRememberUser] = useState(false);

  if (!isOpen) return null; // 모달이 열리지 않으면 렌더링 안함

  return (
    <>
      {/* 어둡게 깔리는 뒷배경 */}
      <div
        onClick={onClose} // 배경 클릭하면 모달 닫기
        style={{
          position: "fixed",
          top: 0, left: 0, right: 0, bottom: 0,
          backgroundColor: "rgba(0, 0, 0, 0.5)", // 반투명 검정
          zIndex: 999,
        }}
      />

      {/* 모달 본체 */}
      <div
        style={{
          position: "fixed",
          top: "50%", left: "50%",
          transform: "translate(-50%, -50%)",
          backgroundColor: "white",
          padding: "2rem",
          borderRadius: "8px",
          zIndex: 1000,
          width: "320px",
          boxShadow: "0 2px 10px rgba(0,0,0,0.2)"
        }}
        onClick={e => e.stopPropagation()} // 모달 내부 클릭 시 배경 닫기 방지
      >
        {/* 닫기 버튼 */}
        <button
          onClick={onClose}
          style={{
            position: "absolute",
            top: "8px",
            right: "8px",
            border: "none",
            background: "transparent",
            fontSize: "1.2rem",
            cursor: "pointer"
          }}
          aria-label="Close modal"
        >
          &times;
        </button>

        <h2 style={{ marginBottom: "1rem" }}>로그인</h2>

        <label>
          유저명<br />
          <input
            type="text"
            value={username}
            onChange={e => setUsername(e.target.value)}
            style={{ width: "100%", padding: "0.4rem" }}
          />
        </label>

        <label style={{ marginTop: "1rem" }}>
          비밀번호<br />
          <input
            type="password"
            value={password}
            onChange={e => setPassword(e.target.value)}
            style={{ width: "100%", padding: "0.4rem" }}
          />
        </label>

        <label style={{ marginTop: "1rem", display: "flex", alignItems: "center" }}>
          <input
            type="checkbox"
            checked={rememberUser}
            onChange={e => setRememberUser(e.target.checked)}
            style={{ marginRight: "0.5rem" }}
          />
          유저명 저장
        </label>

        <button
          onClick={() => {
            // 여기에 로그인 처리 함수 넣기
            alert(`로그인 시도: ${username}, 비밀번호: ${password}, 저장: ${rememberUser}`);
          }}
          style={{
            marginTop: "1.5rem",
            width: "100%",
            padding: "0.6rem",
            backgroundColor: "#007bff",
            color: "white",
            border: "none",
            borderRadius: "4px",
            cursor: "pointer"
          }}
        >
          로그인
        </button>

        <div style={{ marginTop: "1rem", fontSize: "0.9rem", display: "flex", justifyContent: "space-between" }}>
          <a href="/signup" onClick={e => { e.preventDefault(); alert("회원가입 링크 클릭됨"); }} style={{ color: "#007bff", cursor: "pointer" }}>
            회원가입
          </a>
          <a href="/forgot-password" onClick={e => { e.preventDefault(); alert("비밀번호 찾기 링크 클릭됨"); }} style={{ color: "#007bff", cursor: "pointer" }}>
            비밀번호 찾기
          </a>
        </div>
      </div>
    </>
  );
}
