import React, { useState, useEffect, useRef } from "react";
import { FixedSizeList as List } from "react-window";
import PostCard from "../postTest/PostCard";

const ITEM_HEIGHT = 420; // PostCard 예상 높이, 필요하면 조정하세요.
const BUFFER = 40;

const samplePost = {
  nickname: "홍길동",
  title: "첫 게시글입니다!",
  createdAt: "2025-06-13T10:00:00Z",
  updatedAt: "2025-06-13T12:00:00Z",
  contentHtml: `<p>이건 정말 긴 게시글의 예시입니다. <strong>HTML 형식</strong>도 포함돼요.<br>줄바꿈도 됩니다.</p>
                <p>이미지도 가능하고, 다양한 포맷이 적용될 수 있습니다. 여기에 아주 많은 텍스트가 있다고 가정합시다...</p>`,
  thumbnailImage: "https://via.placeholder.com/600x300",
  comments: [
    {
      id: "c1",
      author: "철수",
      content: "게시글 잘 읽었습니다!",
      createdAt: "2025-06-13T11:00:00Z",
      replies: [
        {
          id: "r1",
          author: "영희",
          content: "저도 동감입니다!",
          createdAt: "2025-06-13T11:15:00Z",
        },
      ],
    },
  ],
};

const fakeFetchPost = async (index) => {
  return new Promise((resolve) => {
    setTimeout(() => {
      resolve({
        ...samplePost,
        title: `게시글 ${index} - ${samplePost.title}`,
      });
    }, 100);
  });
};

const ScrollTest = () => {
  const [posts, setPosts] = useState(new Map());
  const [visibleIndexes, setVisibleIndexes] = useState({ start: 0, stop: 0 });
  const listRef = useRef(null);

  useEffect(() => {
    const loadAround = async (startIndex, stopIndex) => {
      const newPosts = new Map(posts);

      for (let i = startIndex; i <= stopIndex; i++) {
        if (!newPosts.has(i)) {
          const post = await fakeFetchPost(i);
          if (post) {
            newPosts.set(i, post);
          }
        }
      }

      const min = Math.max(0, startIndex - BUFFER);
      const max = stopIndex + BUFFER;

      // 메모리 최적화를 위해 범위 밖 게시글 삭제
      for (let key of newPosts.keys()) {
        if (key < min || key > max) {
          newPosts.delete(key);
        }
      }

      setPosts(new Map(newPosts));
    };

    loadAround(visibleIndexes.start, visibleIndexes.stop);
  }, [visibleIndexes]); // posts는 의존성에서 제거

  const Row = ({ index, style }) => {
    const post = posts.get(index);

    return (
      <div style={{ ...style, padding: "10px 0" }}>
        {post ? <PostCard post={post} /> : <div style={{padding:20}}>로딩 중...</div>}
      </div>
    );
  };

  return (
    <List
      height={800}
      itemCount={10000}
      itemSize={ITEM_HEIGHT}
      width="100%"
      ref={listRef}
      onItemsRendered={({ visibleStartIndex, visibleStopIndex }) => {
        setVisibleIndexes({ start: visibleStartIndex, stop: visibleStopIndex });
      }}
      className="scrollbar-none"
    >
      {Row}
    </List>
  );
};

export default ScrollTest;
