import { useState, useEffect } from "react";
import { useDispatch } from "react-redux";
import { registerModal, popModal } from "../../redux/modalStackSlice";
import { setUserInfo } from "../../redux/authSlice";
import { login } from "../../api/auth";
import FocusLock from "react-focus-lock";

export default function SignupModal() {
  const [modalShow, setModalShow] = useState(false);
  const [modalInfo, setModalInfo] = useState({ fade: false, z: 0 });

  const dispatch = useDispatch();

  // ÏûÖÎ†•Í∞í ÏÉÅÌÉú
  const [nickname, setNickname] = useState("");
  const [email, setEmail] = useState("");
  const [password, setPassword] = useState("");
  const [passwordConfirm, setPasswordConfirm] = useState("");

  // ÏóêÎü¨ Î©îÏãúÏßÄ ÏÉÅÌÉú (Îπà Î¨∏ÏûêÏó¥Ïù¥Î©¥ Ï†ïÏÉÅ)
  const [nicknameError, setNicknameError] = useState("");
  const [emailError, setEmailError] = useState("");
  const [passwordError, setPasswordError] = useState("");
  const [passwordConfirmError, setPasswordConfirmError] = useState("");

  const [showPassword, setShowPassword] = useState(false);

  // --- Í≤ÄÏ¶ù Ìï®ÏàòÎì§ ---
  function validateNickname(value) {
    if (!value) return "ÎãâÎÑ§ÏûÑÏùÑ ÏûÖÎ†•Ìï¥Ï£ºÏÑ∏Ïöî.";
    if (value.length < 2 || value.length > 8)
      return "ÎãâÎÑ§ÏûÑÏùÄ 2Ïûê Ïù¥ÏÉÅ, 8Ïûê Ïù¥ÌïòÏó¨Ïïº Ìï©ÎãàÎã§.";
    const pattern = /^(?!.*[!@#$%^&*()+={}[\]|\\:;\"'<>,?/]).+$/;
    if (!pattern.test(value))
      return "ÎãâÎÑ§ÏûÑÏóê ÏÇ¨Ïö© Î∂àÍ∞ÄÌïú ÌäπÏàòÎ¨∏ÏûêÍ∞Ä Ìè¨Ìï®ÎêòÏñ¥ ÏûàÏäµÎãàÎã§.";
    return "";
  }

  function validateEmail(value) {
    if (!value) return "Ïù¥Î©îÏùºÏùÄ ÌïÑÏàòÏûÖÎãàÎã§.";
    // Í∞ÑÎã®Ìïú Ïù¥Î©îÏùº Ï†ïÍ∑úÏãù
    const emailPattern =
      /^[^\s@]+@[^\s@]+\.[^\s@]+$/;
    if (!emailPattern.test(value))
      return "Ïù¥Î©îÏùº ÌòïÏãùÏù¥ Ïò¨Î∞îÎ•¥ÏßÄ ÏïäÏäµÎãàÎã§.";
    return "";
  }

  function validatePassword(value) {
    if (!value) return "ÎπÑÎ∞ÄÎ≤àÌò∏Îäî ÌïÑÏàòÏûÖÎãàÎã§.";
    if (value.length < 8 || value.length > 20)
      return "ÎπÑÎ∞ÄÎ≤àÌò∏Îäî 8Ïûê Ïù¥ÏÉÅ, 20Ïûê Ïù¥ÌïòÏó¨Ïïº Ìï©ÎãàÎã§.";
    // ÎåÄÎ¨∏Ïûê, ÏÜåÎ¨∏Ïûê, Ïà´Ïûê, ÌäπÏàòÎ¨∏Ïûê Ìè¨Ìï® Ï†ïÍ∑úÏãù
    const pwdPattern =
      /^(?=.*[a-z])(?=.*[A-Z])(?=.*\d)(?=.*[!@#$%^&*()\-_])[A-Za-z\d!@#$%^&*()\-_]+$/;
    if (!pwdPattern.test(value))
      return "ÎπÑÎ∞ÄÎ≤àÌò∏Îäî ÎåÄÎ¨∏Ïûê, ÏÜåÎ¨∏Ïûê, Ïà´Ïûê, ÌäπÏàòÎ¨∏Ïûê(!@#$%^&*()-_)Î•º Ìè¨Ìï®Ìï¥Ïïº Ìï©ÎãàÎã§.";
    return "";
  }

  function validatePasswordConfirm(value, pwd) {
    if (!value) return "ÎπÑÎ∞ÄÎ≤àÌò∏Î•º Í≤ÄÏ¶ùÌï¥Ï£ºÏÑ∏Ïöî.";
    if (value !== pwd) return "ÎπÑÎ∞ÄÎ≤àÌò∏ ÌôïÏù∏Ïù¥ ÏùºÏπòÌïòÏßÄ ÏïäÏäµÎãàÎã§.";
    return "";
  }

  // --- Ìï∏Îì§Îü¨ ---
  function handleNicknameChange(e) {
    const val = e.target.value;
    setNickname(val);
    setNicknameError(validateNickname(val));
  }

  function handleEmailChange(e) {
    const val = e.target.value;
    setEmail(val);
    setEmailError(validateEmail(val));
  }

  function handlePasswordChange(e) {
    const val = e.target.value;
    setPassword(val);
    setPasswordError(validatePassword(val));
    // ÎπÑÎ∞ÄÎ≤àÌò∏Í∞Ä Î∞îÎÄåÎ©¥ ÌôïÏù∏ÎèÑ Ïû¨Í≤ÄÏ¶ù
    setPasswordConfirmError(validatePasswordConfirm(passwordConfirm, val));
  }

  function handlePasswordConfirmChange(e) {
    const val = e.target.value;
    setPasswordConfirm(val);
    setPasswordConfirmError(validatePasswordConfirm(val, password));
  }

  async function handleSignup() {
    // ÏµúÏ¢Ö Í≤ÄÏ¶ù
    const ne = validateNickname(nickname);
    const ee = validateEmail(email);
    const pe = validatePassword(password);
    const pce = validatePasswordConfirm(passwordConfirm, password);

    setNicknameError(ne);
    setEmailError(ee);
    setPasswordError(pe);
    setPasswordConfirmError(pce);

    if (ne || ee || pe || pce) return; // ÏóêÎü¨ ÏûàÏúºÎ©¥ submit Ï§ëÎã®

    // Î°úÍ∑∏Ïù∏ API Ìò∏Ï∂ú (ÌòπÏùÄ ÌöåÏõêÍ∞ÄÏûÖ API Ìò∏Ï∂ú)
    let res = await login(email, password);
    if (res !== null) {
      dispatch(
        setUserInfo({
          nickname: res.nickname,
          username: res.username,
          accessToken: res.accessToken,
        })
      );
      dispatch(popModal());
      modalClear();
    }
  }

  function togglePasswordVisibility() {
    setShowPassword((prev) => !prev);
  }

  function modalClear() {
    setNickname("");
    setEmail("");
    setPassword("");
    setPasswordConfirm("");
    setNicknameError("");
    setEmailError("");
    setPasswordError("");
    setPasswordConfirmError("");
    setShowPassword(false);
  }

  useEffect(() => {
    dispatch(
      registerModal({
        modalName: "signup",
        modalClear,
        setModalShow,
        setModalInfo,
      })
    );
  }, [dispatch]);

  // ÌÖåÎëêÎ¶¨ ÏÉâÏÉÅ Í≤∞Ï†ï
  const getBorderColor = (error) => (error ? "border-red-500" : "border-green-500");

  return (
    <div id="signup-modal">
      {modalShow && (
        <div
          className={"fixed inset-0 flex justify-center items-center z-" + modalInfo.z}
          onClick={() => {
            dispatch(popModal());
          }}
        >
          <div
            className={
              "bg-white relative rounded-lg p-6 w-96 max-h-[80vh] overflow-y-auto" +
