import { useState, useEffect } from "react";
import { useDispatch } from "react-redux";
import { registerModal, popModal } from "../../redux/modalStackSlice";
import { setUserInfo } from "../../redux/authSlice";
import { login } from "../../api/auth";
import FocusLock from "react-focus-lock";

export default function SignupModal() {
  const [modalShow, setModalShow] = useState(false);
  const [modalInfo, setModalInfo] = useState({ fade: false, z: 0 });

  const dispatch = useDispatch();

  const [email, setEmail] = useState("");
  const [password, setPassword] = useState("");
  const [passwordConfirm, setPasswordConfirm] = useState("");
  const [nickname, setNickname] = useState("");
  const [showPassword, setShowPassword] = useState(false);

  // Í≤ÄÏ¶ù Î©îÏãúÏßÄ ÏÉÅÌÉú
  const [emailError, setEmailError] = useState("");
  const [passwordError, setPasswordError] = useState("");
  const [passwordConfirmError, setPasswordConfirmError] = useState("");
  const [nicknameError, setNicknameError] = useState("");

  // DTO Í∏∞Î∞ò Í≤ÄÏ¶ù Ìï®ÏàòÎì§

  // Ïù¥Î©îÏùº Í≤ÄÏ¶ù (NotBlank + Email)
  function validateEmail(value) {
    if (!value.trim()) return "Ïù¥Î©îÏùºÏùÄ ÌïÑÏàòÏûÖÎãàÎã§.";
    // Í∞ÑÎã®Ìïú Ïù¥Î©îÏùº ÌòïÏãù Ï≤¥ÌÅ¨
    const emailRegex = /^[^\s@]+@[^\s@]+\.[^\s@]+$/;
    if (!emailRegex.test(value)) return "Ïù¥Î©îÏùº ÌòïÏãùÏù¥ Ïò¨Î∞îÎ•¥ÏßÄ ÏïäÏäµÎãàÎã§.";
    return "";
  }

  // ÎπÑÎ∞ÄÎ≤àÌò∏ Í≤ÄÏ¶ù (NotBlank + Size 8~20 + Ìå®ÌÑ¥)
  function validatePassword(value) {
    if (!value.trim()) return "ÎπÑÎ∞ÄÎ≤àÌò∏Îäî ÌïÑÏàòÏûÖÎãàÎã§.";
    if (value.length < 8 || value.length > 20)
      return "ÎπÑÎ∞ÄÎ≤àÌò∏Îäî 8Ïûê Ïù¥ÏÉÅ, 20Ïûê Ïù¥ÌïòÏó¨Ïïº Ìï©ÎãàÎã§.";
    const pattern = /^(?=.*[a-z])(?=.*[A-Z])(?=.*\d)(?=.*[!@#$%^&*()\-_])[A-Za-z\d!@#$%^&*()\-_]+$/;
    if (!pattern.test(value))
      return "ÎπÑÎ∞ÄÎ≤àÌò∏Îäî ÎåÄÎ¨∏Ïûê, ÏÜåÎ¨∏Ïûê, Ïà´Ïûê, ÌäπÏàòÎ¨∏Ïûê(!@#$%^&*()-_)Î•º Ìè¨Ìï®Ìï¥Ïïº Ìï©ÎãàÎã§.";
    return "";
  }

  // ÎπÑÎ∞ÄÎ≤àÌò∏ ÌôïÏù∏ (NotBlank + ÎπÑÎ∞ÄÎ≤àÌò∏ÏôÄ ÏùºÏπò)
  function validatePasswordConfirm(value, pw) {
    if (!value.trim()) return "ÎπÑÎ∞ÄÎ≤àÌò∏Î•º Í≤ÄÏ¶ùÌï¥Ï£ºÏÑ∏Ïöî.";
    if (value !== pw) return "ÎπÑÎ∞ÄÎ≤àÌò∏Í∞Ä ÏùºÏπòÌïòÏßÄ ÏïäÏäµÎãàÎã§.";
    return "";
  }

  // ÎãâÎÑ§ÏûÑ Í≤ÄÏ¶ù (NotBlank + Size 2~8 + ÌäπÏàòÎ¨∏Ïûê Ï†úÏô∏)
  function validateNickname(value) {
    if (!value.trim()) return "ÎãâÎÑ§ÏûÑÏùÑ ÏûÖÎ†•Ìï¥Ï£ºÏÑ∏Ïöî.";
    if (value.length < 2 || value.length > 8)
      return "ÎãâÎÑ§ÏûÑÏùÄ 2Ïûê Ïù¥ÏÉÅ, 8Ïûê Ïù¥ÌïòÏó¨Ïïº Ìï©ÎãàÎã§.";
    // ÌäπÏàòÎ¨∏Ïûê Î∂àÍ∞Ä Ï†ïÍ∑úÏãù (DTOÏùò Í≤ÉÍ≥º ÎèôÏùº)
    const pattern = /^(?!.*[!@#$%^&*()+={}[\]|\\:;\"'<>,?/]).+$/;
    if (!pattern.test(value))
      return "ÎãâÎÑ§ÏûÑÏóê ÏÇ¨Ïö© Î∂àÍ∞ÄÌïú ÌäπÏàòÎ¨∏ÏûêÍ∞Ä Ìè¨Ìï®ÎêòÏñ¥ ÏûàÏäµÎãàÎã§.";
    return "";
  }

  // ÏûÖÎ†•Í∞í Î≥ÄÍ≤Ω Ïãú Í≤ÄÏ¶ù Ïã§Ìñâ
  useEffect(() => {
    setEmailError(validateEmail(email));
  }, [email]);

  useEffect(() => {
    setPasswordError(validatePassword(password));
    // ÎπÑÎ∞ÄÎ≤àÌò∏Í∞Ä Î∞îÎÄåÎ©¥ ÌôïÏù∏ÎèÑ Í∞ôÏù¥ Í≤ÄÏÇ¨
    setPasswordConfirmError(validatePasswordConfirm(passwordConfirm, password));
  }, [password]);

  useEffect(() => {
    setPasswordConfirmError(validatePasswordConfirm(passwordConfirm, password));
  }, [passwordConfirm, password]);

  useEffect(() => {
    setNicknameError(validateNickname(nickname));
  }, [nickname]);

  async function handleSignup() {
    // ÏµúÏ¢Ö Ï†úÏ∂ú Ï†Ñ Ï†ÑÏ≤¥ Í≤ÄÏ¶ù
    const eError = validateEmail(email);
    const pError = validatePassword(password);
    const pcError = validatePasswordConfirm(passwordConfirm, password);
    const nError = validateNickname(nickname);

    setEmailError(eError);
    setPasswordError(pError);
    setPasswordConfirmError(pcError);
    setNicknameError(nError);

    if (eError || pError || pcError || nError) {
      return; // ÏóêÎü¨ ÏûàÏúºÎ©¥ Ï†úÏ∂ú Ï§ëÎã®
    }

    let res = await login(email, password);
    if (res !== null) {
      dispatch(
        setUserInfo({
          nickname: res.nickname,
          username: res.username,
          accessToken: res.accessToken,
        })
      );
      dispatch(popModal());
    }
  }

  function togglePasswordVisibility() {
    setShowPassword((prev) => !prev);
  }

  function modalClear() {
    setEmail("");
    setPassword("");
    setPasswordConfirm("");
    setNickname("");
    setShowPassword(false);

    setEmailError("");
    setPasswordError("");
    setPasswordConfirmError("");
    setNicknameError("");
  }

  useEffect(() => {
    dispatch(
      registerModal({
        modalName: "signup",
        modalClear,
        setModalShow,
        setModalInfo,
      })
    );
  }, [dispatch]);

  // ÎßêÌíçÏÑ† Ïä§ÌÉÄÏùº (z-index: modalInfo.z + 10)
  const tooltipStyle = {
    position: "absolute",
    backgroundColor: "rgba(255, 0, 0, 0.9)",
    color: "white",
    fontSize: "0.8rem",
    padding: "0.3rem 0.5rem",
    borderRadius: "0.3rem",
    top: "100%",
    left: 0,
    marginTop: "2px",
    zIndex: modalInfo.z + 10,
    whiteSpace: "nowrap",
  };

  return (
    <div id="signup-modal">
      {modalShow && (
        <div
          className={"fixed inset-0 flex justify-center items-center z-" + modalInfo.z}
          onClick={() => {
            dispatch(popModal());
          }}
        >
          <div
            className={
              "bg-white relative rounded-lg p-6 w-96 max-h-[80vh] overflow-y-auto" +
              (modalInfo.fade
                ? " fixed inset-0 bg-black bg-opacity-30 backdrop-blur-sm"
                : "")
            }
            onClick={(e) => e.stopPropagation()}
          >
            <button
              className="absolute top-2 right-2 px-2 py-0.8 bg-gray-100 rounded hover:bg-gray-200"
