import { useState, useEffect } from "react";
import { useDispatch } from "react-redux";
import { registerModal, popModal } from "../../redux/modalStackSlice";
import { setUserInfo } from "../../redux/authSlice";
import { login } from "../../api/auth";
import FocusLock from "react-focus-lock";

export default function SignupModal() {
  const [modalShow, setModalShow] = useState(false);
  const [modalInfo, setModalInfo] = useState({ fade: false, z: 0 });

  const dispatch = useDispatch();

  const [email, setEmail] = useState("");
  const [password, setPassword] = useState("");
  const [passwordConfirm, setPasswordConfirm] = useState("");
  const [nickname, setNickname] = useState("");
  const [showPassword, setShowPassword] = useState(false);

  // 검증 메시지 상태
  const [emailError, setEmailError] = useState("");
  const [passwordError, setPasswordError] = useState("");
  const [passwordConfirmError, setPasswordConfirmError] = useState("");
  const [nicknameError, setNicknameError] = useState("");

  // DTO 기반 검증 함수들

  // 이메일 검증 (NotBlank + Email)
  function validateEmail(value) {
    if (!value.trim()) return "이메일은 필수입니다.";
    // 간단한 이메일 형식 체크
    const emailRegex = /^[^\s@]+@[^\s@]+\.[^\s@]+$/;
    if (!emailRegex.test(value)) return "이메일 형식이 올바르지 않습니다.";
    return "";
  }

  // 비밀번호 검증 (NotBlank + Size 8~20 + 패턴)
  function validatePassword(value) {
    if (!value.trim()) return "비밀번호는 필수입니다.";
    if (value.length < 8 || value.length > 20)
      return "비밀번호는 8자 이상, 20자 이하여야 합니다.";
    const pattern = /^(?=.*[a-z])(?=.*[A-Z])(?=.*\d)(?=.*[!@#$%^&*()\-_])[A-Za-z\d!@#$%^&*()\-_]+$/;
    if (!pattern.test(value))
      return "비밀번호는 대문자, 소문자, 숫자, 특수문자(!@#$%^&*()-_)를 포함해야 합니다.";
    return "";
  }

  // 비밀번호 확인 (NotBlank + 비밀번호와 일치)
  function validatePasswordConfirm(value, pw) {
    if (!value.trim()) return "비밀번호를 검증해주세요.";
    if (value !== pw) return "비밀번호가 일치하지 않습니다.";
    return "";
  }

  // 닉네임 검증 (NotBlank + Size 2~8 + 특수문자 제외)
  function validateNickname(value) {
    if (!value.trim()) return "닉네임을 입력해주세요.";
    if (value.length < 2 || value.length > 8)
      return "닉네임은 2자 이상, 8자 이하여야 합니다.";
    // 특수문자 불가 정규식 (DTO의 것과 동일)
    const pattern = /^(?!.*[!@#$%^&*()+={}[\]|\\:;\"'<>,?/]).+$/;
    if (!pattern.test(value))
      return "닉네임에 사용 불가한 특수문자가 포함되어 있습니다.";
    return "";
  }

  // 입력값 변경 시 검증 실행
  useEffect(() => {
    setEmailError(validateEmail(email));
  }, [email]);

  useEffect(() => {
    setPasswordError(validatePassword(password));
    // 비밀번호가 바뀌면 확인도 같이 검사
    setPasswordConfirmError(validatePasswordConfirm(passwordConfirm, password));
  }, [password]);

  useEffect(() => {
    setPasswordConfirmError(validatePasswordConfirm(passwordConfirm, password));
  }, [passwordConfirm, password]);

  useEffect(() => {
    setNicknameError(validateNickname(nickname));
  }, [nickname]);

  async function handleSignup() {
    // 최종 제출 전 전체 검증
    const eError = validateEmail(email);
    const pError = validatePassword(password);
    const pcError = validatePasswordConfirm(passwordConfirm, password);
    const nError = validateNickname(nickname);

    setEmailError(eError);
    setPasswordError(pError);
    setPasswordConfirmError(pcError);
    setNicknameError(nError);

    if (eError || pError || pcError || nError) {
      return; // 에러 있으면 제출 중단
    }

    let res = await login(email, password);
    if (res !== null) {
      dispatch(
        setUserInfo({
          nickname: res.nickname,
          username: res.username,
          accessToken: res.accessToken,
        })
      );
      dispatch(popModal());
    }
  }

  function togglePasswordVisibility() {
    setShowPassword((prev) => !prev);
  }

  function modalClear() {
    setEmail("");
    setPassword("");
    setPasswordConfirm("");
    setNickname("");
    setShowPassword(false);

    setEmailError("");
    setPasswordError("");
    setPasswordConfirmError("");
    setNicknameError("");
  }

  useEffect(() => {
    dispatch(
      registerModal({
        modalName: "signup",
        modalClear,
        setModalShow,
        setModalInfo,
      })
    );
  }, [dispatch]);

  // 말풍선 스타일 (z-index: modalInfo.z + 10)
  const tooltipStyle = {
    position: "absolute",
    backgroundColor: "rgba(255, 0, 0, 0.9)",
    color: "white",
    fontSize: "0.8rem",
    padding: "0.3rem 0.5rem",
    borderRadius: "0.3rem",
    top: "100%",
    left: 0,
    marginTop: "2px",
    zIndex: modalInfo.z + 10,
    whiteSpace: "nowrap",
  };

  return (
    <div id="signup-modal">
      {modalShow && (
        <div
          className={"fixed inset-0 flex justify-center items-center z-" + modalInfo.z}
          onClick={() => {
            dispatch(popModal());
          }}
        >
          <div
            className={
              "bg-white relative rounded-lg p-6 w-96 max-h-[80vh] overflow-y-auto" +
              (modalInfo.fade
                ? " fixed inset-0 bg-black bg-opacity-30 backdrop-blur-sm"
                : "")
            }
            onClick={(e) => e.stopPropagation()}
          >
            <button
              className="absolute top-2 right-2 px-2 py-0.8 bg-gray-100 rounded hover:bg-gray-200"
              onClick={() => {
                dispatch(popModal());
              }}
            >
              ×
            </button>

            <h2 className="text-xl font-semibold mb-4">회원가입</h2>
            <FocusLock>
              <form
                onSubmit={async (e) => {
                  e.preventDefault();
                  await handleSignup();
                }}
                className="flex flex-col space-y-6 items-start relative"
              >
                {/* 닉네임 */}
                <div className="flex flex-col w-full relative">
                  <label htmlFor="signup-nickname" className="mb-1">
                    닉네임
                  </label>
                  <input
                    type="text"
                    id="signup-nickname"
                    placeholder="닉네임"
                    autoComplete="off"
                    className={`w-full px-4 py-2 border rounded-md focus:outline-none focus:ring-2 ${
                      nicknameError ? "border-red-600 focus:ring-red-400" : "border-gray-300 focus:ring-indigo-300"
                    }`}
                    required
                    value={nickname}
                    onChange={(e) => setNickname(e.target.value)}
                  />
                  {nicknameError && <div style={tooltipStyle}>{nicknameError}</div>}
                </div>

                {/* 이메일 */}
                <div className="flex flex-col w-full relative">
                  <label htmlFor="signup-email" className="mb-1">
                    이메일
                  </label>
                  <input
                    type="email"
                    id="signup-email"
                    placeholder="이메일"
                    autoComplete="new-email"
                    className={`w-full px-4 py-2 border rounded-md focus:outline-none focus:ring-2 ${
                      emailError ? "border-red-600 focus:ring-red-400" : "border-gray-300 focus:ring-indigo-300"
                    }`}
                    required
                    value={email}
                    onChange={(e) => setEmail(e.target.value)}
                  />
                  {emailError && <div style={tooltipStyle}>{emailError}</div>}
                </div>

                {/* 비밀번호 */}
                <div className="flex flex-col w-full relative">
                  <label htmlFor="signup-password" className="mb-1">
                    비밀번호
                  </label>
                  <div className="relative">
                    <input
                      type={showPassword ? "text" : "password"}
                      id="signup-password"
                      placeholder="비밀번호"
                      autoComplete="new-password"
                      className={`w-full px-4 py-2 pr-10 border rounded-md focus:outline-none focus:ring-2 ${
                        passwordError ? "border-red-600 focus:ring-red-400" : "border-gray-300 focus:ring-indigo-300"
                      }`}
                      required
                      value={password}
                      onChange={(e) => setPassword(e.target.value)}
                    />
                    <button
                      type="button"
                      onClick={togglePasswordVisibility}
                      className="absolute right-2 top-1/2 transform -translate-y-1/2 text-gray-500 hover:text-gray-700"
                      tabIndex={-1}
                    >
                      {showPassword ? "🕶️" : "👀"}
                    </button>
                  </div>
                  {passwordError && <div style={tooltipStyle}>{passwordError}</div>}
                </div>

                {/* 비밀번호 확인 */}
                <div className="flex flex-col w-full relative">
                  <label htmlFor="signup-password-confirm" className="mb-1">
                    비밀번호 확인
                  </label>
                  <input
                    type={showPassword ? "text" : "password"}
                    id="signup-password-confirm"
                    placeholder="비밀번호 확인"
                    autoComplete="new-password"
                    className={`w-full px-4 py-2 border rounded-md focus:outline-none focus:ring-2 ${
                      passwordConfirmError ? "border-red-600 focus:ring-red-400" : "border-gray-300 focus:ring-indigo-300"
                    }`}
                    required
                    value={passwordConfirm}
                    onChange={(e) => setPasswordConfirm(e.target.value)}
                  />
                  {passwordConfirmError && <div style={tooltipStyle}>{passwordConfirmError}</div>}
                </div>

                <button
                  type="submit"
                  className="w-full py-2 bg-indigo-600 text-white rounded hover:bg-indigo-700"
                >
                  가입하기
                </button>
              </form>
            </FocusLock>
          </div>
        </div>
      )}
    </div>
  );
}
