import React, { useState, useEffect, useCallback, useRef } from "react";
import { FixedSizeList as List } from "react-window";
import axios from "axios";

const ITEM_HEIGHT = 150;
const BUFFER = 40;

const ScrollTest = () => {
  const [posts, setPosts] = useState(new Map());
  const [visibleIndexes, setVisibleIndexes] = useState({ start: 0, stop: 0 });
  const listRef = useRef(null);

  const fetchPost_real = async (index) => {
    try {
      const res = await axios.get(`/api/posts/${index}`);
      return res.data;
    } catch (e) {
      console.error("Fetch failed", e);
      return null;
    }
  };

  const fetchPost = async (index) => {
  // 실제 서버 요청은 생략하고, 테스트용 데이터 반환
  return {
    title: `게시글 ${index}`,
    previewText: `이것은 ${index}번 게시글의 미리보기입니다.`,
  };
};


  const loadAround = useCallback(async (startIndex, stopIndex) => {
    const newPosts = new Map(posts);

    for (let i = startIndex; i <= stopIndex; i++) {
      if (!posts.has(i)) {
        const post = await fetchPost(i);
        if (post) newPosts.set(i, post);
      }
    }

    const min = Math.max(0, startIndex - BUFFER);
    const max = stopIndex + BUFFER;

    for (let key of newPosts.keys()) {
      if (key < min || key > max) {
        newPosts.delete(key);
      }
    }

    setPosts(new Map(newPosts));
  }, [posts]);

  useEffect(() => {
    loadAround(visibleIndexes.start, visibleIndexes.stop);
  }, [visibleIndexes, loadAround]);

  const Row = ({ index, style }) => {
    const post = posts.get(index);

    if (!post) {
      return <div style={style}>로딩 중...</div>;
    }

    return (
      <div style={{ ...style, padding: 10, borderBottom: "1px solid #ccc" }}>
        <h3>{post.title}</h3>
        <p>{post.previewText}</p>
      </div>
    );
  };

  return (
    <List
      height={600}
      itemCount={10000}
      itemSize={ITEM_HEIGHT}
      width="100%"
      ref={listRef}
      onItemsRendered={({ visibleStartIndex, visibleStopIndex }) => {
        setVisibleIndexes({ start: visibleStartIndex, stop: visibleStopIndex });
      }}
    >
      {Row}
    </List>
  );
};

export default ScrollTest;
