import React from "react";
import { Editor } from "react-draft-wysiwyg";
import { ContentBlock } from "draft-js";

function imageBlockRenderer(contentBlock) {
  const type = contentBlock.getType();
  if (type === "atomic") {
    return {
      component: MediaComponent,
      editable: false,
      props: {
        foo: "bar", // 필요 시
      },
    };
  }
  return null;
}

function MediaComponent({ block, contentState, blockProps }) {
  const entity = contentState.getEntity(block.getEntityAt(0));
  const { src, alt, height, width } = entity.getData();

  return (
    <div>
      <img
        src={src}
        alt={alt || ""}
        style={{ height: height || "auto", width: width || "auto" }}
      />
    </div>
  );
}

// 에디터 컴포넌트 내에서
<Editor
  editorState={editorState}
  onEditorStateChange={onEditorStateChange}
  customBlockRenderFunc={imageBlockRenderer}
  toolbar={{
    image: {
      uploadCallback: uploadImageCallback,
      previewImage: true,
      alt: { present: true, mandatory: false },
    },
  }}
/>
