import React, { useState } from 'react';
import { Card, CardContent } from './ui/card';
import { Button } from './ui/button';

export default function PostCard({ post }) {
  const [expanded, setExpanded] = useState(false);
  const [showComments, setShowComments] = useState(false);

  const {
    nickname,
    title,
    createdAt,
    updatedAt,
    contentHtml,
    thumbnailImage,
    comments = [],
  } = post;

  const isModified = updatedAt && updatedAt !== createdAt;
  const truncatedContent = contentHtml.slice(0, 300) + '...';
  const isContentTruncated = contentHtml.length > 300;

  return (
    <Card className="mb-6 transition-all duration-300">
      <CardContent>
        <div className="mb-2 text-sm text-gray-600">
          <span className="font-semibold">{nickname}</span> ·{' '}
          <span>{new Date(createdAt).toLocaleString()}</span>
          {isModified && (
            <span className="ml-2 text-xs text-gray-400">(수정됨)</span>
          )}
        </div>

        <h2 className="text-lg font-bold mb-2">{title}</h2>

        {thumbnailImage && (
          <img
            src={thumbnailImage}
            alt="thumbnail"
            className="mb-2 w-full rounded max-h-64 object-cover"
          />
        )}

        <div
          className="text-sm text-gray-800"
          dangerouslySetInnerHTML={{
            __html: expanded ? contentHtml : truncatedContent,
          }}
        />

        {isContentTruncated && (
          <p
            className="mt-1 text-blue-500 text-sm cursor-pointer hover:underline inline-block"
            onClick={() => setExpanded(!expanded)}
          >
            {expanded ? '접기' : '더보기'}
          </p>
        )}

        <div className="mt-4 flex space-x-2">
          <Button onClick={() => alert('추천!')}>👍 추천</Button>
          <Button onClick={() => alert('비추천!')} className="bg-red-500 hover:bg-red-600">
            👎 비추천
          </Button>
          <Button
            onClick={() => setShowComments((prev) => !prev)}
            className="bg-gray-500 hover:bg-gray-600"
          >
            {showComments ? '💬 댓글 닫기' : '💬 댓글 보기'}
          </Button>
        </div>

        {/* 댓글 표시 영역 */}
        <div
          className={`
            transition-all duration-500 ease-in-out overflow-hidden
            ${showComments ? 'max-h-[500px] mt-4' : 'max-h-0'}
          `}
        >
          <div className="p-4 border rounded bg-gray-50 overflow-y-auto max-h-[500px]">
            <h3 className="text-sm font-semibold mb-2">댓글</h3>
            <ul className="text-sm text-gray-700 space-y-4">
              {comments.map((comment) => (
                <li key={comment.id}>
                  <div>
                    <strong>{comment.author}</strong>: {comment.content}
                  </div>
                  {comment.replies?.length > 0 && (
                    <ul className="ml-4 mt-2 space-y-2 border-l-2 border-gray-200 pl-4">
                      {comment.replies.map((reply) => (
                        <li key={reply.id}>
                          <span className="text-gray-600">
                            <strong>{reply.author}</strong>: {reply.content}
                          </span>
                        </li>
                      ))}
                    </ul>
                  )}
                </li>
              ))}
            </ul>
          </div>
        </div>
      </CardContent>
    </Card>
  );
}
