import React, { useState, useRef, useLayoutEffect } from 'react';
import { Card, CardContent } from './ui/card';
import { Button } from './ui/button';
import CommentsSection from './CommentsSection';

export default function PostCard({ post }) {
  const [expanded, setExpanded] = useState(false);
  const [showComments, setShowComments] = useState(false);
  const [repliesToggledMap, setRepliesToggledMap] = useState({});

  const [animatedHeight, setAnimatedHeight] = useState(0); // Ìï≠ÏÉÅ Ïà´Ïûê (px)
  const [heightAuto, setHeightAuto] = useState(false); // auto ÏÉÅÌÉú ÌîåÎûòÍ∑∏

  const commentsRef = useRef(null);

  const {
    nickname,
    title,
    createdAt,
    updatedAt,
    contentHtml,
    thumbnailImage,
    comments = [],
  } = post;

  const isModified = updatedAt && updatedAt !== createdAt;
  const truncatedContent = contentHtml.slice(0, 300) + '...';
  const isContentTruncated = contentHtml.length > 300;

  // ÎåìÍ∏ÄÏ∞Ω Ïó¥Î¶º/Îã´Ìûò Î∞è ÎãµÍ∏Ä ÌÜ†Í∏ÄÏãú ÎÜíÏù¥ Í≥ÑÏÇ∞ Î∞è Ïï†ÎãàÎ©îÏù¥ÏÖò Ï≤òÎ¶¨
  useLayoutEffect(() => {
    if (!commentsRef.current) return;

    if (showComments) {
      // Ïó¥Î¶¥ Îïå: ÎÜíÏù¥ ÌîΩÏÖÄÍ∞íÏúºÎ°ú ÏÑ∏ÌåÖ ‚Üí Ìä∏ÎûúÏßÄÏÖò ÌõÑ auto ÌîåÎûòÍ∑∏ ÏºúÍ∏∞
      const scrollHeight = commentsRef.current.scrollHeight;
      setHeightAuto(false); // Î®ºÏ†Ä auto ÎÅÑÍ≥†
      setAnimatedHeight(scrollHeight);
    } else {
      // Îã´Ìûê Îïå: autoÎùºÎ©¥ ÌîΩÏÖÄ ÎÜíÏù¥Î°ú Î≥ÄÍ≤Ω ÌõÑ 0ÏúºÎ°ú Ï§ÑÏûÑ
      if (heightAuto) {
        // auto ÏÉÅÌÉúÎ©¥ ÌîΩÏÖÄ ÎÜíÏù¥Î°ú Î∞îÍæº Îí§ Îã§Ïùå tickÏóê 0ÏúºÎ°ú
        const currentHeight = commentsRef.current.scrollHeight;
        setHeightAuto(false);
        setAnimatedHeight(currentHeight);

        // Îã§Ïùå tickÏóêÏÑú 0ÏúºÎ°ú Ï§ÑÏûÑ
        requestAnimationFrame(() => {
          setAnimatedHeight(0);
        });
      } else {
        // Ïù¥ÎØ∏ Ïà´Ïûê ÏÉÅÌÉúÎ©¥ Î∞îÎ°ú 0ÏúºÎ°ú
        setAnimatedHeight(0);
      }
    }
  }, [showComments, repliesToggledMap, comments, heightAuto]);

  // Ìä∏ÎûúÏßÄÏÖò Ï¢ÖÎ£åÏãú auto ÎÜíÏù¥Î°ú Ï†ÑÌôò (Ïó¥Î†∏ÏùÑ ÎïåÎßå)
  function onTransitionEnd() {
    if (showComments) {
      setHeightAuto(true);
      setAnimatedHeight(0); // Ïã§Ï†úÎ°ú Ïä§ÌÉÄÏùºÏóêÏÑú heightÎäî autoÎ°ú Î∞îÎÄú
    }
  }

  function handleRepliesToggle(commentId, isOpen) {
    setRepliesToggledMap((prev) => ({
      ...prev,
      [commentId]: isOpen,
    }));
  }

  return (
    <Card className="mb-6 transition-all duration-300">
      <CardContent>
        <div className="mb-2 text-sm text-gray-600">
          <span className="font-semibold">{nickname}</span> ¬∑{' '}
          <span>{new Date(createdAt).toLocaleString()}</span>
          {isModified && (
            <span className="ml-2 text-xs text-gray-400">(ÏàòÏ†ïÎê®)</span>
          )}
        </div>

        <h2 className="text-lg font-bold mb-2">{title}</h2>

        {thumbnailImage && (
          <img
            src={thumbnailImage}
            alt="thumbnail"
            className="mb-2 w-full rounded max-h-64 object-cover"
          />
        )}

        <div
          className="text-sm text-gray-800"
          dangerouslySetInnerHTML={{
            __html: expanded ? contentHtml : truncatedContent,
          }}
        />

        {isContentTruncated && (
          <p
            className="mt-1 text-blue-500 text-sm cursor-pointer hover:underline inline-block"
            onClick={() => setExpanded(!expanded)}
          >
            {expanded ? 'Ï†ëÍ∏∞' : 'ÎçîÎ≥¥Í∏∞'}
          </p>
        )}

        <div className="mt-4 flex space-x-2">
          <Button onClick={() => alert('Ï∂îÏ≤ú!')}>üëç Ï∂îÏ≤ú</Button>
          <Button
            onClick={() => alert('ÎπÑÏ∂îÏ≤ú!')}
            className="bg-red-500 hover:bg-red-600"
          >
            üëé ÎπÑÏ∂îÏ≤ú
          </Button>
          <Button
            onClick={() => setShowComments((prev) => !prev)}
            className="bg-gray-500 hover:bg-gray-600"
          >
            {showComments ? 'üí¨ ÎåìÍ∏Ä Îã´Í∏∞' : 'üí¨ ÎåìÍ∏Ä Î≥¥Í∏∞'}
          </Button>
        </div>

        <div
          ref={commentsRef}
          style={{
            height: heightAuto ? 'auto' : animatedHeight,
            overflow: 'hidden',
            transition: 'height 300ms ease',
          }}
          className="mt-4"
          aria-expanded={showComments}
          onTransitionEnd={onTransitionEnd}
        >
          <CommentsSection comments={comments} onToggle={handleRepliesToggle} />
        </div>
      </CardContent>
    </Card>
  );
}
