import { modalPush, modalPop } from './ModalStack';
import { useState } from 'react';

const modalName = "LoginModal";

export default function LoginModal() 
{
    const [isOpen, setIsOpen] = useState(false);

    function modalClear()
    {
        // 모달 클리어하는 함수
        console.log('로그인 모달 클리어함');
    }

    const modalObj = { 'name' : modalName, "setIsOpen" : setIsOpen, "modalClear" : modalClear };

    function handleModalOpen()
    {
        modalPush(modalObj);
    }

  return (
    <>
      <button
        className="px-4 py-2 bg-blue-600 text-white rounded hover:bg-blue-700"
        onClick={handleModalOpen}
      >
        모달 열기
      </button>

      {isOpen && (
        // 전체화면 배경 (반투명 검정)
        <div
          className="fixed inset-0 bg-black bg-opacity-30 flex justify-center items-center z-50"
          onClick={toggleIsOpen} // 배경 클릭 시 닫기
        >
          {/* 모달 박스 */}
          <div
            className="bg-white relative rounded-lg p-6 w-96 max-h-[80vh] overflow-y-auto"
            onClick={(e) => e.stopPropagation()} // 모달 내부 클릭 시 배경 클릭 이벤트 차단
          >
            <button
              className="absolute top-2 right-2 px-2 py-0.8 bg-gray-100 rounded hover:bg-gray-200"
              onClick={modalPush}
            >
                ×
            </button>

            <h2 className="text-xl font-semibold mb-4">로그인</h2>
            <p>내용은 여기에 </p>
          </div>
        </div>
      )}
    </>
  );
}
