import React, { useState, useEffect, useRef } from 'react';
import { Card, CardContent } from './ui/card';
import CommentsSection from './CommentsSection';

// ÎÇ†Ïßú Ìè¨Îß∑ÌåÖ Ìï®Ïàò
function formatDateTime(timestamp) {
  if (!timestamp) return '';
  const date = new Date(timestamp);

  const yyyy = date.getFullYear();
  const mm = date.getMonth() + 1;
  const dd = date.getDate();

  let hh = date.getHours();
  const min = date.getMinutes().toString().padStart(2, '0');

  const ampm = hh < 12 ? 'Ïò§Ï†Ñ' : 'Ïò§ÌõÑ';
  hh = hh % 12;
  if (hh === 0) hh = 12;

  return `${yyyy}. ${mm}. ${dd}. ${ampm} ${hh}:${min}`;
}

// HTMLÏùÑ ÏïàÏ†ÑÌïòÍ≤å ÏûêÎ•¥Îêò <img> ÌÉúÍ∑∏Îäî Ï†úÍ±∞ÌïòÎäî Ìï®Ïàò
function truncateHtmlSafely(html, maxLength) {
  const div = document.createElement('div');
  div.innerHTML = html;

  // Ïù¥ÎØ∏ÏßÄ ÌÉúÍ∑∏ Ï†úÍ±∞
  const imgs = div.querySelectorAll('img');
  imgs.forEach((img) => img.remove());

  let currentLength = 0;

  function walk(node) {
    if (currentLength >= maxLength) return null;

    if (node.nodeType === Node.TEXT_NODE) {
      const remaining = maxLength - currentLength;
      const text = node.textContent.slice(0, remaining);
      currentLength += text.length;
      return document.createTextNode(text);
    }

    if (node.nodeType === Node.ELEMENT_NODE) {
      const clone = node.cloneNode(false);
      const children = Array.from(node.childNodes);

      for (let child of children) {
        const result = walk(child);
        if (result) clone.appendChild(result);
        if (currentLength >= maxLength) break;
      }

      return clone;
    }

    return null;
  }

  const result = walk(div);
  const container = document.createElement('div');
  if (result) container.appendChild(result);

  return container.innerHTML;
}

const COMMENTS_FIXED_HEIGHT = 384; // px
const MAX_PREVIEW_HEIGHT = 200; // px - ÎØ∏Î¶¨Î≥¥Í∏∞ ÏòÅÏó≠ ÏµúÎåÄ ÎÜíÏù¥

export default function PostCard({ post }) {
  const [expanded, setExpanded] = useState(false);
  const [showComments, setShowComments] = useState(false);
  const [repliesToggledMap, setRepliesToggledMap] = useState({});
  const [animatedHeight, setAnimatedHeight] = useState(0);
  const [needsTruncation, setNeedsTruncation] = useState(false);

  const fullContentRef = useRef(null); // Ïù¥ÎØ∏ÏßÄ Ìè¨Ìï® Ï†ÑÏ≤¥ Î≥∏Î¨∏ ÎÜíÏù¥ Ï≤¥ÌÅ¨Ïö©
  const previewRef = useRef(null);     // Ïù¥ÎØ∏ÏßÄ Ï†úÏô∏ ÎØ∏Î¶¨Î≥¥Í∏∞ Î≥∏Î¨∏ ÎÜíÏù¥ Ï≤¥ÌÅ¨Ïö©

  const {
    author_nickname: nickname,
    title,
    content_html: contentHtml,
    thumbnail_img_url: thumbnailImage,
    comments = [],
    created_at,
    updated_at,
  } = post;

  const createdAt = created_at ? created_at * 1000 : null;
  const updatedAt = updated_at !== null && updated_at !== undefined ? updated_at * 1000 : null;
  const isModified = updatedAt && updatedAt !== createdAt;

  useEffect(() => {
    if (fullContentRef.current) {
      const fullHeight = fullContentRef.current.scrollHeight;
      setNeedsTruncation(fullHeight > MAX_PREVIEW_HEIGHT);
    }
  }, [contentHtml]);

  // ÎåìÍ∏Ä ÏòÅÏó≠ Ïï†ÎãàÎ©îÏù¥ÏÖò Ï≤òÎ¶¨
  useEffect(() => {
    if (showComments) {
      setAnimatedHeight(0);
      const timeoutId = setTimeout(() => {
        setAnimatedHeight(COMMENTS_FIXED_HEIGHT);
      }, 10);
      return () => clearTimeout(timeoutId);
    } else {
      setAnimatedHeight(0);
    }
  }, [showComments]);

  function toggleExpanded() {
    setExpanded((prev) => !prev);
  }
  function onToggleClick(e) {
    e.stopPropagation();
    toggleExpanded();
  }
  function handleRepliesToggle(commentId, isOpen) {
    setRepliesToggledMap((prev) => ({
      ...prev,
      [commentId]: isOpen,
    }));
  }

  return (
    <Card className="mb-6 transition-all duration-300 relative">
      <CardContent>
        <div className="mb-2 text-sm text-gray-600">
          <span className="font-semibold">{nickname}</span> ¬∑{' '}
          <span>{createdAt ? formatDateTime(createdAt) : ''}</span>
          {isModified && (
            <span className="ml-2 text-xs text-gray-400">
              (ÏàòÏ†ïÎê®: {formatDateTime(updatedAt)})
            </span>
          )}
        </div>

        <h2 className="text-lg font-bold mb-2">{title}</h2>

        {/* Ïç∏ÎÑ§Ïùº: Ï†ëÌûå ÏÉÅÌÉúÏóêÏÑúÎßå, ÎçîÎ≥¥Í∏∞ ÌïÑÏöîÌï† ÎïåÎßå ÎÖ∏Ï∂ú */}
        {!expanded && needsTruncation && thumbnailImage && (
          <div className="flex justify-center mb-2">
            <img
              src={thumbnailImage}
              alt="thumbnail"
              className="rounded max-h-64 object-cover"
            />
          </div>
        )}

        {/* Î≥∏Î¨∏ ÏòÅÏó≠ */}
        <div
          className="prose prose-sm max-w-none text-gray-800 cursor-pointer select-text group"
          onClick={toggleExpanded}
          title={needsTruncation ? (expanded ? 'Ï†ëÍ∏∞' : 'ÎçîÎ≥¥Í∏∞') : undefined}
          aria-expanded={expanded}
          style={{
            maxHeight: !expanded && needsTruncation ? MAX_PREVIEW_HEIGHT : 'none',
            overflow: !expanded && needsTruncation ? 'hidden' : 'visible',
          }}
        >
          {/* Ïù¥ÎØ∏ÏßÄ Ìè¨Ìï® Î≥∏Î¨∏ (Ïà®ÍπÄÏö© ÎÜíÏù¥ Ï≤¥ÌÅ¨Ïö©) */}
          <div
            ref={fullContentRef}
            style={{
              position: 'absolute',
              visibility: 'hidden',
              height: 'auto',
              overflow: 'visible',
              pointerEvents: 'none',
              whiteSpace: 'normal',
              width: 'calc(100% - 1rem)', // ÎåÄÎûµ Î≥∏Î¨∏ ÏòÅÏó≠ ÎÑàÎπÑ ÎßûÏ∂§
            }}
            dangerouslySetInnerHTML={{ __html: contentHtml }}
          />

          {/* ÎØ∏Î¶¨Î≥¥Í∏∞Ïö©: Ïù¥ÎØ∏ÏßÄ Ï†úÍ±∞Ìïú Î≥∏Î¨∏ ÎòêÎäî Ï†ÑÏ≤¥ Î≥∏Î¨∏ */}
          <div
            ref={previewRef}
            dangerouslySetInnerHTML={{
              __html: !expanded && needsTruncation
                ? truncateHtmlSafely(contentHtml, 10000)
                : contentHtml,
            }}
          />
        </div>

        {/* ÎçîÎ≥¥Í∏∞/Ï†ëÍ∏∞ ÌëúÏãú */}
        {needsTruncation && (
          <span
            onClick={onToggleClick}
            className="text-blue-500 ml-1 cursor-pointer group-hover:underline"
            role="button"
            tabIndex={0}
            onKeyPress={(e) => {
              if (e.key === 'Enter') onToggleClick(e);
            }}
          >
            {expanded ? 'Ï†ëÍ∏∞' : '... ÎçîÎ≥¥Í∏∞'}
          </span>
        )}

        {/* Ï∂îÏ≤ú / ÎπÑÏ∂îÏ≤ú Î≤ÑÌäº */}
        <div className="mt-4 flex justify-between">
          <div className="flex space-x-2">
            <button onClick={() => alert('Ï∂îÏ≤ú!')}>üëç Ï∂îÏ≤ú</button>
            <button
              onClick={() => alert('ÎπÑÏ∂îÏ≤ú!')}
              className="bg-red-500 hover:bg-red-600"
            >
              üëé ÎπÑÏ∂îÏ≤ú
            </button>
          </div>

          {/* ÎåìÍ∏Ä Î≥¥Í∏∞ ÌÜ†Í∏Ä Î≤ÑÌäº */}
          <button
            onClick={() => setShowComments((prev) => !prev)}
            className="bg-gray-500 hover:bg-gray-600"
          >
            {showComments ? 'üí¨ ÎåìÍ∏Ä Îã´Í∏∞' : 'üí¨ ÎåìÍ∏Ä Î≥¥Í∏∞'}
          </button>
        </div>

        {/* ÎåìÍ∏Ä ÏòÅÏó≠ Ïï†ÎãàÎ©îÏù¥ÏÖò ÎÜíÏù¥ Ï°∞Ï†à */}
        <div
          style={{
            height: animatedHeight,
            transition: 'height 300ms ease',
            overflow: 'hidden',
          }}
          className="mt-4"
          aria-expanded={showComments}
        >
          {showComments && (
            <CommentsSection
              comments={comments}
              onToggle={handleRepliesToggle}
            />
          )}
        </div>
      </CardContent>
    </Card>
  );
}
