import { useState } from "react";

export default function SignupTemp() {
  const [password, setPassword] = useState("");
  const [showPassword, setShowPassword] = useState(false);
  const [isFocused, setIsFocused] = useState(false);

  const togglePasswordVisibility = () => setShowPassword((prev) => !prev);

  const allowedCharsRegex = /^[A-Za-z\d!@#$%^&*()\-_]+$/;
  const fullPasswordRegex = /^(?=.*[a-z])(?=.*[A-Z])(?=.*\d)(?=.*[!@#$%^&*()\-_])[A-Za-z\d!@#$%^&*()\-_]+$/;

  const passwordConditions = {
    length: password.length >= 8 && password.length <= 20,
    hasUpper: /[A-Z]/.test(password),
    hasLower: /[a-z]/.test(password),
    hasNumber: /\d/.test(password),
    hasSpecial: /[!@#$%^&*()\-_]/.test(password),
    onlyAllowedChars: allowedCharsRegex.test(password),
  };

  const allValid = Object.values(passwordConditions).every((v) => v);

  return (
    <div className="min-h-screen flex items-center justify-center bg-gray-100 px-4">
      <div className="bg-white p-6 rounded shadow-md w-full max-w-md">
        <h2 className="text-xl font-bold mb-4">비밀번호 조건 테스트</h2>

        <div className="flex items-center gap-x-2 w-full relative">
          <span className="mr-2">🔒</span>
          <div className="relative flex-1 min-w-0">
            <input
              type={showPassword ? "text" : "password"}
              placeholder="비밀번호"
              className={`w-full px-4 py-2 pr-10 border rounded-md focus:outline-none 
                ${allValid ? "border-green-400 ring-2 ring-green-300" : "border-gray-300 focus:ring-2 focus:ring-indigo-300"}`}
              value={password}
              onChange={(e) => setPassword(e.target.value)}
              onFocus={() => setIsFocused(true)}
              onBlur={() => setIsFocused(false)}
            />
            <button
              type="button"
              onClick={togglePasswordVisibility}
              className="absolute right-2 top-1/2 transform -translate-y-1/2 text-gray-500 hover:text-gray-700"
              tabIndex={-1}
            >
              {showPassword ? "🕶️" : "👀"}
            </button>

            {/* 조건 말풍선 */}
            {isFocused && (
              <div className={`absolute top-full left-0 mt-2 w-full bg-white border shadow-md rounded p-3 text-sm z-10 
                ${allValid ? "border-green-400 ring-1 ring-green-300" : "border-gray-300"}`}>
                <p className="mb-1 font-semibold text-gray-800">비밀번호 조건:</p>
                <ul className="space-y-1 text-gray-700">
                  <li className={passwordConditions.length ? "text-green-600" : "text-gray-500"}>
                    {passwordConditions.length ? "✅" : "⬜"} 8~20자 사이
                  </li>
                  <li className={passwordConditions.hasUpper ? "text-green-600" : "text-gray-500"}>
                    {passwordConditions.hasUpper ? "✅" : "⬜"} 대문자 포함
                  </li>
                  <li className={passwordConditions.hasLower ? "text-green-600" : "text-gray-500"}>
                    {passwordConditions.hasLower ? "✅" : "⬜"} 소문자 포함
                  </li>
                  <li className={passwordConditions.hasNumber ? "text-green-600" : "text-gray-500"}>
                    {passwordConditions.hasNumber ? "✅" : "⬜"} 숫자 포함
                  </li>
                  <li className={passwordConditions.hasSpecial ? "text-green-600" : "text-gray-500"}>
                    {passwordConditions.hasSpecial ? "✅" : "⬜"} 특수문자 포함 (!@#$%^&*()-_)
                  </li>
                  <li className={password.length > 0 && !passwordConditions.onlyAllowedChars ? "text-red-600" : "text-green-600"}>
                    {password.length > 0 && !passwordConditions.onlyAllowedChars ? "❌" : "✅"} 허용된 문자만 사용
                  </li>
                </ul>
              </div>
            )}
          </div>
        </div>
      </div>
    </div>
  );
}
