import { modalRegister, modalPush, modalPop } from './ModalManager';
import { useState } from 'react';

const modalName = "LoginModal";

export default function LoginModal() 
{
    const [isOpen, setIsOpen] = useState(false);

    function modalClear()
    {
        // 모달 클리어하는 함수
        console.log('로그인 모달 클리어함');
    }

    modalRegister({ 'name' : modalName, "setIsOpen" : setIsOpen, "modalClear" : modalClear });

    async function handleLogin() { await login(document.getElementById("id").value, document.getElementById("password").value); }

  return (
    <>
      {isOpen && (
        <div
          className="fixed inset-0 bg-black bg-opacity-30 flex justify-center items-center z-50"
          onClick={modalPop}>

          <div
            className="bg-white relative rounded-lg p-6 w-96 max-h-[80vh] overflow-y-auto"
            onClick={(e) => e.stopPropagation()}>
            <button
              className="absolute top-2 right-2 px-2 py-0.8 bg-gray-100 rounded hover:bg-gray-200"
              onClick={modalPop}>
                ×
            </button>

            <h2 className="text-xl font-semibold mb-4">로그인</h2>
            <div className="login-test">
                <input type="text" id="id"></input>
                <input type="password" id="password"></input>
                <button onClick={handleLogin}>로그인 테스트</button>
            </div>

          </div>
        </div>
      )}
    </>
  );
}
