import React, { useState, useEffect, useRef } from 'react';
import { Card, CardContent } from './ui/card';
import CommentsSection from './CommentsSection';

// 날짜 포맷팅 함수
function formatDateTime(timestamp) {
  if (!timestamp) return '';
  const date = new Date(timestamp);

  const yyyy = date.getFullYear();
  const mm = date.getMonth() + 1;
  const dd = date.getDate();

  let hh = date.getHours();
  const min = date.getMinutes().toString().padStart(2, '0');

  const ampm = hh < 12 ? '오전' : '오후';
  hh = hh % 12;
  if (hh === 0) hh = 12;

  return `${yyyy}. ${mm}. ${dd}. ${ampm} ${hh}:${min}`;
}

// 이미지 태그 제거하고 안전하게 글자수 제한하는 함수
function truncateHtmlSafely(html, maxLength) {
  const div = document.createElement('div');
  div.innerHTML = html;

  const imgs = div.querySelectorAll('img');
  imgs.forEach((img) => img.remove());

  let currentLength = 0;

  function walk(node) {
    if (currentLength >= maxLength) return null;

    if (node.nodeType === Node.TEXT_NODE) {
      const remaining = maxLength - currentLength;
      const text = node.textContent.slice(0, remaining);
      currentLength += text.length;
      return document.createTextNode(text);
    }

    if (node.nodeType === Node.ELEMENT_NODE) {
      const clone = node.cloneNode(false);
      const children = Array.from(node.childNodes);
      for (let child of children) {
        const result = walk(child);
        if (result) clone.appendChild(result);
        if (currentLength >= maxLength) break;
      }
      return clone;
    }

    return null;
  }

  const result = walk(div);
  const container = document.createElement('div');
  if (result) container.appendChild(result);

  return container.innerHTML;
}

const COMMENTS_FIXED_HEIGHT = 384; // px
const PREVIEW_MAX_LENGTH = 300;   // 미리보기 글자수 제한 (조절 가능)

export default function PostCard({ post }) {
  const [expanded, setExpanded] = useState(false);
  const [showComments, setShowComments] = useState(false);
  const [repliesToggledMap, setRepliesToggledMap] = useState({});
  const [animatedHeight, setAnimatedHeight] = useState(0);
  const [needsTruncation, setNeedsTruncation] = useState(false);

  const {
    author_nickname: nickname,
    title,
    content_html: contentHtml,
    thumbnail_img_url: thumbnailImage,
    comments = [],
    created_at,
    updated_at,
  } = post;

  const createdAt = created_at ? created_at * 1000 : null;
  const updatedAt = updated_at !== null && updated_at !== undefined ? updated_at * 1000 : null;
  const isModified = updatedAt && updatedAt !== createdAt;

  // 본문 텍스트 기반으로 길이 체크 후 needsTruncation 결정 (약 300자 기준)
  useEffect(() => {
    // 텍스트만 추출
    const tempDiv = document.createElement('div');
    tempDiv.innerHTML = contentHtml;
    // 이미지 제거
    tempDiv.querySelectorAll('img').forEach((img) => img.remove());
    const textContent = tempDiv.textContent || tempDiv.innerText || '';
    setNeedsTruncation(textContent.length > PREVIEW_MAX_LENGTH);
  }, [contentHtml]);

  // 미리보기 HTML (본문 글자 제한 후)
  // 더보기 텍스트는 본문에 자연스럽게 이어짐 (본문 마지막에 '...'는 텍스트로 추가, '더보기'는 클릭가능한 링크로 분리)
  function getPreviewWithMoreLink() {
    if (!needsTruncation) {
      return contentHtml;
    }

    // 글자수 제한하여 잘라내기 (이미지 제외)
    const truncatedHtml = truncateHtmlSafely(contentHtml, PREVIEW_MAX_LENGTH);

    // 뒤에 '... ' 붙이고 그 뒤에 클릭가능한 '더보기' 붙임
    // '...'는 본문 스타일 유지, '더보기'는 파란색 링크 스타일
    // React에서는 HTML 조작이 까다로우므로 아래 JSX 분리해서 렌더링
    return truncatedHtml;
  }

  // 본문 클릭 시 처리: 접기 상태이고, 본문이 길 경우만 펼치기 동작
  function onContentClick() {
    if (!expanded && needsTruncation) {
      setExpanded(true);
    }
  }

  // 접기 버튼 클릭 시 처리 (접기 버튼은 따로)
  function onCollapseClick(e) {
    e.stopPropagation();
    setExpanded(false);
  }

  // 썸네일 클릭 시도 본문 펼치기 효과와 동일
  function onThumbnailClick() {
    if (!expanded && needsTruncation) {
      setExpanded(true);
    }
  }

  return (
    <Card className="mb-6 transition-all duration-300">
      <CardContent>
        {/* 작성자 및 날짜 */}
        <div className="mb-2 text-sm text-gray-600">
          <span className="font-semibold">{nickname}</span> ·{' '}
          <span>{createdAt ? formatDateTime(createdAt) : ''}</span>
          {isModified && (
            <span className="ml-2 text-xs text-gray-400">
              (수정됨: {formatDateTime(updatedAt)})
            </span>
          )}
        </div>

        {/* 제목 */}
        <h2 className="text-lg font-bold mb-2">{title}</h2>

        {/* 썸네일 (접기 상태이고, 본문이 길 때만 보여줌) */}
        {!expanded && needsTruncation && thumbnailImage && (
          <div
            className="flex justify-center mb-2 cursor-pointer"
            onClick={onThumbnailClick}
            role="button"
            tabIndex={0}
            onKeyDown={(e) => {
              if (e.key === 'Enter') onThumbnailClick();
            }}
            aria-label="썸네일 클릭시 본문 펼치기"
          >
            <img
              src={thumbnailImage}
              alt="thumbnail"
              className="rounded max-h-64 object-cover"
              draggable={false}
            />
          </div>
        )}

        {/* 본문 */}
        <div
          className="prose prose-sm max-w-none text-gray-800 select-text relative"
          onClick={onContentClick}
          role={needsTruncation && !expanded ? 'button' : undefined}
          tabIndex={needsTruncation && !expanded ? 0 : undefined}
          onKeyDown={(e) => {
            if ((e.key === 'Enter' || e.key === ' ') && needsTruncation && !expanded) {
              e.preventDefault();
              onContentClick();
            }
          }}
          aria-expanded={expanded}
          aria-label={
            needsTruncation && !expanded
              ? '본문 일부가 숨겨져 있습니다. 클릭하면 전체 내용을 볼 수 있습니다.'
              : undefined
          }
          style={{ whiteSpace: 'pre-wrap', cursor: needsTruncation && !expanded ? 'pointer' : 'default' }}
        >
          {expanded ? (
            // 전체 본문
            <div dangerouslySetInnerHTML={{ __html: contentHtml }} />
          ) : (
            // 미리보기 + ... + 더보기 링크를 JSX로 분리해서 자연스럽게 표현
            <>
              {/* 본문 미리보기 */}
              <span
                dangerouslySetInnerHTML={{ __html: getPreviewWithMoreLink() }}
              />
              {/* '...'는 본문 텍스트와 동일 스타일로, 공백과 붙여서 */}
              <span>... </span>
              {/* 더보기 링크 스타일 */}
              <span
                onClick={(e) => {
                  e.stopPropagation();
                  setExpanded(true);
                }}
                onKeyDown={(e) => {
                  if (e.key === 'Enter' || e.key === ' ') {
                    e.preventDefault();
                    e.stopPropagation();
                    setExpanded(true);
                  }
                }}
                role="link"
                tabIndex={0}
                className="text-blue-600 hover:underline cursor-pointer select-text"
                aria-label="더보기"
                style={{ userSelect: 'none' }}
              >
                더보기
              </span>
            </>
          )}
        </div>

        {/* 접기 버튼 (expanded 상태에서만 노출) */}
        {expanded && (
          <div className="mt-1">
            <button
              onClick={onCollapseClick}
              className="text-blue-600 hover:underline cursor-pointer select-text bg-transparent border-none p-0"
              aria-label="본문 접기"
            >
              접기
            </button>
          </div>
        )}

        {/* 추천/비추천 및 댓글 토글 */}
        <div className="mt-4 flex justify-between">
          <div className="flex space-x-2">
            <button onClick={() => alert('추천!')}>👍 추천</button>
            <button
              onClick={() => alert('비추천!')}
              className="bg-red-500 hover:bg-red-600 text-white"
            >
              👎 비추천
            </button>
          </div>

          <button
            onClick={() => setShowComments((prev) => !prev)}
            className="bg-gray-500 hover:bg-gray-600 text-white"
          >
            {showComments ? '💬 댓글 닫기' : '💬 댓글 보기'}
          </button>
        </div>

        {/* 댓글 영역 애니메이션 높이 */}
        <div
          style={{
            height: animatedHeight,
            transition: 'height 300ms ease',
            overflow: 'hidden',
          }}
          className="mt-4"
          aria-expanded={showComments}
        >
          {showComments && (
            <CommentsSection comments={comments} onToggle={handleRepliesToggle} />
          )}
        </div>
      </CardContent>
    </Card>
  );
}
