import React, { useState, useEffect, useRef, useCallback } from 'react';
import { FixedSizeList as List } from 'react-window';

const DUMMY_FEED = Array.from({ length: 100 }, (_, i) => ({
  id: i + 1,
  title: `게시글 제목 #${i + 1}`,
  content: `게시글 내용입니다. 이것은 테스트 데이터입니다. #${i + 1}`,
}));

const PAGE_SIZE = 10;

export default function VirtualizedInfiniteFeed() {
  const [items, setItems] = useState(DUMMY_FEED.slice(0, PAGE_SIZE));
  const [hasMore, setHasMore] = useState(true);

  // 더 로드하는 함수
  const loadMoreItems = useCallback(() => {
    if (!hasMore) return;
    setTimeout(() => {
      setItems((prevItems) => {
        const nextItems = DUMMY_FEED.slice(prevItems.length, prevItems.length + PAGE_SIZE);
        if (nextItems.length === 0) {
          setHasMore(false);
          return prevItems;
        }
        return [...prevItems, ...nextItems];
      });
    }, 500); // 로딩 딜레이 시뮬레이션
  }, [hasMore]);

  // 리스트가 스크롤 끝에 도달하면 loadMoreItems 호출
  const handleItemsRendered = ({ visibleStopIndex }) => {
    if (visibleStopIndex >= items.length - 1 && hasMore) {
      loadMoreItems();
    }
  };

  // 리스트 아이템 렌더러
  const Row = ({ index, style }) => {
    const item = items[index];
    if (!item) return <div style={style}>로딩 중...</div>;

    return (
      <div
        style={{
          ...style,
          borderBottom: '1px solid #ddd',
          padding: 12,
          boxSizing: 'border-box',
          backgroundColor: index % 2 === 0 ? '#fafafa' : 'white',
        }}
      >
        <h3>{item.title}</h3>
        <p>{item.content}</p>
      </div>
    );
  };

  return (
    <div style={{ maxWidth: 600, margin: 'auto' }}>
      <h2>Virtualized 무한 스크롤 피드</h2>
      <List
        height={600}          // 리스트 높이
        itemCount={items.length}
        itemSize={100}        // 각 아이템 높이(px)
        width={'100%'}
        onItemsRendered={handleItemsRendered}
      >
        {Row}
      </List>
      {!hasMore && <div style={{ textAlign: 'center', padding: 20 }}>마지막입니다.</div>}
    </div>
  );
}
