import React, { useState, useEffect, useRef, useCallback } from 'react';
import PostCard from './PostCard';

const PAGE_SIZE = 20;
const MAX_POSTS = 40;

export default function ArticleTest() {
  const [posts, setPosts] = useState([]);
  const [page, setPage] = useState(0);
  const [isLoading, setIsLoading] = useState(false);
  const loadMoreRef = useRef();

  const fetchPosts = useCallback(async (pageNum) => {
    setIsLoading(true);

    // 실제 API 호출 부분 (임시 더미 데이터 사용 중)
    const newPosts = Array(PAGE_SIZE).fill(null).map((_, i) => {
      const id = pageNum * PAGE_SIZE + i + 1;
      return {
        id: `post_${id}`,
        nickname: `홍길동_${id}`,
        title: `게시글 제목 ${id}`,
        contentHtml: `<p>게시글 내용 예시 ${id}.</p>`,
        thumbnailImage: 'https://via.placeholder.com/600x300',
        comments: [],
      };
    });

    setPosts((prev) => {
      const combined = [...prev, ...newPosts];
      if (combined.length > MAX_POSTS) {
        return combined.slice(combined.length - MAX_POSTS);
      }
      return combined;
    });

    setIsLoading(false);
  }, []);

  // 무한 스크롤 감지
  useEffect(() => {
    if (!loadMoreRef.current) return;

    const observer = new IntersectionObserver(
      ([entry]) => {
        if (entry.isIntersecting && !isLoading) {
          setPage((prev) => prev + 1);
        }
      },
      { rootMargin: '200px' }
    );

    observer.observe(loadMoreRef.current);

    return () => {
      if (loadMoreRef.current) observer.unobserve(loadMoreRef.current);
    };
  }, [isLoading]);

  // page가 바뀔 때마다 게시글 fetch
  useEffect(() => {
    fetchPosts(page);
  }, [page, fetchPosts]);

  // 초기 데이터 로드
  useEffect(() => {
    setPage(0);
  }, []);

  return (
    <div className="max-w-xl mx-auto mt-10 px-4">
      {posts.map((post) => (
        <PostCard key={post.id} post={post} />
      ))}

      {/* 로딩 감지용 div */}
      <div ref={loadMoreRef} style={{ height: 1 }} />

      {/* 로딩 중일 때 스피너 표시 */}
      {isLoading && (
        <div className="flex justify-center py-6">
          <div className="animate-spin rounded-full h-8 w-8 border-t-2 border-b-2 border-blue-500" />
        </div>
      )}
    </div>
  );
}
