// components/modal/NewLoginModal.jsx
import { useEffect, useState } from "react";
import { useDispatch } from "react-redux";
import { login } from "../../api/auth";
import { registerModal, showModal, popModal } from "../../redux/modalStackSlice";
import { setUserInfo } from "../../redux/authSlice";
import FocusLock from "react-focus-lock";
import BaseModal from "./BaseModal";

export default function NewLoginModal() {
  const dispatch = useDispatch();

  // 모달 상태
  const [show, setShow] = useState(false);

  // 로그인 상태
  const [email, setEmail] = useState("");
  const [password, setPassword] = useState("");
  const [loginErrorMessage, setLoginErrorMessage] = useState(null);

  function clearModal() {
    setEmail("");
    setPassword("");
    setLoginErrorMessage(null);
  }

  async function handleLogin() {
    const result = await login(email, password);
    if (result) {
      dispatch(setUserInfo(result));
      dispatch(popModal());
    } else {
      setLoginErrorMessage("아이디 또는 비밀번호가 올바르지 않습니다.");
    }
  }

  // 모달 등록
  useEffect(() => {
    dispatch(registerModal({ name: "login", clear: clearModal, setShow }));
  }, [dispatch]);

  if (!show) return null;

  return (
    <div id="login-modal">
      <BaseModal title="로그인">
        <FocusLock>
          <form
            onSubmit={async (e) => {
              e.preventDefault();
              await handleLogin();
            }}
            className="flex flex-col space-y-4 items-center"
          >
            <div className="flex items-center gap-x-2 w-full">
              <span className="mr-2">📧</span>
              <input
                type="text"
                placeholder="email"
                value={email}
                onChange={(e) => setEmail(e.target.value)}
                autoComplete="new-email"
                className="flex-1 min-w-0 px-4 py-2 border border-gray-300 rounded-md focus:outline-none focus:ring-2 focus:ring-indigo-300"
                required
              />
            </div>

            <div className="flex items-center gap-x-2 w-full">
              <span className="mr-2">🔒</span>
              <input
                type="password"
                placeholder="password"
                value={password}
                onChange={(e) => setPassword(e.target.value)}
                autoComplete="new-password"
                className="flex-1 min-w-0 px-4 py-2 border border-gray-300 rounded-md focus:outline-none focus:ring-2 focus:ring-indigo-300"
                required
              />
            </div>

            {loginErrorMessage && (
              <p className="text-red-500 text-sm">{loginErrorMessage}</p>
            )}

            <button
              type="submit"
              className="w-full bg-indigo-500 text-white px-4 py-2 rounded hover:bg-indigo-700"
            >
              로그인
            </button>
          </form>
        </FocusLock>

        <div className="mt-4 text-center">
          <a
            onClick={() => dispatch(showModal("signup"))}
            className="text-indigo-600 underline hover:text-indigo-800 cursor-pointer"
          >
            처음이신가요?
          </a>
        </div>
      </BaseModal>
    </div>
  );
}
