import { useEffect, useState } from "react";
import { useDispatch } from "react-redux";
import { login } from "../../api/auth";
import { registerModal, showModal, popModal } from "../../redux/modalStackSlice";
import { setUserInfo } from "../../redux/authSlice";
import FocusLock from "react-focus-lock";

export default function NewLoginModal()
{
    // ÎîîÏä§Ìå®Ïπò
    const dispatch = useDispatch();

    // ÏÉÅÌÉú
    const [show, setShow] = useState(false);
    const [email, setEmail] = useState("");
    const [password, setPassword] = useState("");
    const [loginErrorMessage, setLoginErrorMessage] = useState(null);

    // Ìï®Ïàò
    function clearModal() { setEmail(""); setPassword(""); setLoginErrorMessage(null); }

    async function handleLogin()
    {
        let loginResult = await login(email, password);
        if (loginResult !== null)
        {
            dispatch(setUserInfo({ 'nickname' : loginResult.nickname, 'userId' : loginResult.userId, 'accessToken' : loginResult.accessToken }));
            dispatch(popModal());
        }
        else setLoginErrorMessage('a');
    }

    // Ïù¥ÌéôÌä∏
    useEffect(() =>
    {
        dispatch(registerModal({ 'name' : 'login', 'clear' : clearModal, 'setShow' : setShow }));
    }, [dispatch]);

    return(
        <div id="login-modal">
            {show && (

            <div 
            className="fixed inset-0 flex justify-center items-center z-20" 
            onClick={() => {dispatch(popModal()); }}>
                <div 
                className="bg-white relative rounded-lg p-6 w-96 max-h-[80vh] overflow-y-auto"
                onClick={(e) => e.stopPropagation()}>
                    <button 
                    className="absolute top-2 right-2 px-2 py-0.8 bg-gray-100 rounded hover:bg-gray-200" 
                    onClick={() => {dispatch(popModal()); }}>√ó</button>
                    <h2 className="text-xl font-semibold mb-4">Î°úÍ∑∏Ïù∏</h2>
                    <FocusLock>
                        <form
              onSubmit={async (e) => {
                e.preventDefault(); // ÏÉàÎ°úÍ≥†Ïπ® ÎßâÍ∏∞
                await handleLogin();
              }}
              className="flex flex-col space-y-4 items-center"
            >
              <div className="flex items-center gap-x-2 w-full">
                  <span className="mr-2">üìß</span>
                <input
                  type="text"
                  id="login-email"
                  placeholder="email"
                  autoComplete="new-email"
                  className="flex-1 min-w-0 px-4 py-2 border border-gray-300 rounded-md focus:outline-none focus:ring-2 focus:ring-indigo-300"
                  required
                />
              </div>

              <div className="flex items-center gap-x-2 w-full">
                <span className="mr-2">üîí</span>
                <input
                  type="password"
                  id="login-password"
                  placeholder="password"
                  autoComplete="new-password"
                  className="flex-1 min-w-0 px-4 py-2 border border-gray-300 rounded-md focus:outline-none focus:ring-2 focus:ring-indigo-300"
                  required
                />
              </div>

              { loginErrorMessage &&
              (<p className="text-red-500">ÏïÑÏù¥Îîî ÎòêÎäî ÎπÑÎ∞ÄÎ≤àÌò∏Í∞Ä Ïò¨Î∞îÎ•¥ÏßÄ ÏïäÏäµÎãàÎã§.</p>)
              }
              <button
                type="submit"
                className="w-full bg-indigo-500 text-white px-4 py-2 rounded hover:bg-indigo-700"
              >
                Î°úÍ∑∏Ïù∏
              </button>
            </form>
                    </FocusLock>

                </div>
            </div>

            )}
        </div>
    );



}
