export default function CommentsSection({ comments }) {
  return (
    <div className="p-4 border rounded bg-gray-50 overflow-y-auto max-h-96">
      <h3 className="text-sm font-semibold mb-2">댓글</h3>
      <ul className="text-sm text-gray-700 space-y-4">
        {comments.map((comment) => (
          <li key={comment.id}>
            <div className="flex justify-between items-center">
              <strong>{comment.author}</strong>
              <span className="text-xs text-gray-400">
                {new Date(comment.createdAt).toLocaleString()}
              </span>
            </div>
            <div>{comment.content}</div>
            {comment.replies?.length > 0 && (
              <ul className="ml-4 mt-2 space-y-2 border-l-2 border-gray-200 pl-4">
                {comment.replies.map((reply) => (
                  <li key={reply.id}>
                    <div className="flex justify-between items-center text-gray-600 text-xs">
                      <strong>{reply.author}</strong>
                      <span>{new Date(reply.createdAt).toLocaleString()}</span>
                    </div>
                    <div>{reply.content}</div>
                  </li>
                ))}
              </ul>
            )}
          </li>
        ))}
      </ul>
    </div>
  );
}
