import React, { useState, useEffect, useRef } from 'react';

const DUMMY_FEED = Array.from({ length: 100 }, (_, i) => ({
  id: i + 1,
  title: `게시글 제목 #${i + 1}`,
  content: `게시글 내용입니다. 이것은 테스트 데이터입니다. #${i + 1}`,
}));

const PAGE_SIZE = 10;

export default function ScrollTemp() {
  const [items, setItems] = useState(DUMMY_FEED.slice(0, PAGE_SIZE));
  const [page, setPage] = useState(1);
  const loader = useRef(null);

  // 스크롤 감지용 IntersectionObserver
  useEffect(() => {
    const observer = new IntersectionObserver(handleObserver, {
      root: null, // viewport
      rootMargin: '20px',
      threshold: 1.0,
    });
    if (loader.current) {
      observer.observe(loader.current);
    }
    return () => {
      if (loader.current) observer.unobserve(loader.current);
    };
  }, []);

  const handleObserver = (entities) => {
    const target = entities[0];
    if (target.isIntersecting) {
      loadMore();
    }
  };

  const loadMore = () => {
    const nextPage = page + 1;
    const start = (nextPage - 1) * PAGE_SIZE;
    const end = start + PAGE_SIZE;
    const nextItems = DUMMY_FEED.slice(start, end);
    if (nextItems.length > 0) {
      setItems((prev) => [...prev, ...nextItems]);
      setPage(nextPage);
    }
  };

  return (
    <div style={{ maxWidth: 600, margin: 'auto', padding: 20 }}>
      <h2>무한 스크롤 피드</h2>
      {items.map((item) => (
        <div
          key={item.id}
          style={{
            border: '1px solid #ddd',
            padding: 12,
            marginBottom: 12,
            borderRadius: 4,
            background: '#fafafa',
          }}
        >
          <h3>{item.title}</h3>
          <p>{item.content}</p>
        </div>
      ))}

      <div ref={loader} style={{ height: 40, textAlign: 'center' }}>
        {items.length < DUMMY_FEED.length ? '더 불러오는 중...' : '마지막입니다.'}
      </div>
    </div>
  );
}
