import { useEffect, useState } from "react";
import { useDispatch } from "react-redux";
import { login } from "../../api/auth";
import { registerModal, showModal, popModal } from "../../redux/modalStackSlice";
import { setUserInfo } from "../../redux/authSlice";
import FocusLock from "react-focus-lock";
import Modal from "./Modal";

export default function NewLoginModal()
{
    // 디스패치
    const dispatch = useDispatch();

    // 상태
    const [show, setShow] = useState(false);
    const [email, setEmail] = useState("");
    const [password, setPassword] = useState("");
    const [loginErrorMessage, setLoginErrorMessage] = useState(null);

    // 함수
    function clear() { setEmail(""); setPassword(""); setLoginErrorMessage(null); }

    async function handleLogin()
    {
        let loginResult = await login(email, password);
        if (loginResult !== null)
        {
            dispatch(setUserInfo({ 'nickname' : loginResult.nickname, 'userId' : loginResult.userId, 'accessToken' : loginResult.accessToken }));
            dispatch(popModal());
        }
        else setLoginErrorMessage('a');
    }

    return(
    <Modal modalName={"login"} title={"로그인"} clear={clear}>
        <FocusLock>
            <form
              onSubmit={async (e) => {
                e.preventDefault(); // 새로고침 막기
                await handleLogin();
              }}
              className="flex flex-col space-y-4 items-center"
            >
              <div className="flex items-center gap-x-2 w-full">
                  <span className="mr-2">📧</span>
                <input
                  type="text"
                  id="login-email"
                  placeholder="email"
                  autoComplete="new-email"
                  className="flex-1 min-w-0 px-4 py-2 border border-gray-300 rounded-md focus:outline-none focus:ring-2 focus:ring-indigo-300"
                  required
                />
              </div>

              <div className="flex items-center gap-x-2 w-full">
                <span className="mr-2">🔒</span>
                <input
                  type="password"
                  id="login-password"
                  placeholder="password"
                  autoComplete="new-password"
                  className="flex-1 min-w-0 px-4 py-2 border border-gray-300 rounded-md focus:outline-none focus:ring-2 focus:ring-indigo-300"
                  required
                />
              </div>

              { loginFailed &&
              (<p className="text-red-500">아이디 또는 비밀번호가 올바르지 않습니다.</p>)
              }
              <button
                type="submit"
                className="w-full bg-indigo-500 text-white px-4 py-2 rounded hover:bg-indigo-700"
              >
                로그인
              </button>
            </form>
          </FocusLock>
          <div className="mt-4 text-center">
            <a onClick={() => { dispatch(showModal('signup')); }}
              className="text-indigo-600 underline hover:text-indigo-800 cursor-pointer">
              처음이신가요?
            </a>
          </div>
    </Modal>
    );



}
