import React, { useState, useEffect, useRef } from "react";
import { Card, CardContent } from "@/components/ui/card";
import { Button } from "@/components/ui/button";
import { Dialog, DialogContent } from "@/components/ui/dialog";
import { ThumbsUp, ThumbsDown, MessageSquare } from "lucide-react";

const mockFetchPosts = async (page) => {
  // 가짜 데이터 페이징
  return Array.from({ length: 10 }, (_, i) => {
    const id = page * 10 + i;
    return {
      id,
      nickname: `User${id}`,
      title: `게시글 제목 ${id}`,
      createdAt: new Date().toLocaleString(),
      updatedAt: id % 3 === 0 ? new Date().toLocaleString() : null,
      content: `<p>이것은 게시글 <strong>${id}</strong>의 내용입니다.</p><img src="https://via.placeholder.com/400x200" />`,
    };
  });
};

const PostCard = ({ post, onOpenComments }) => {
  const [expanded, setExpanded] = useState(false);
  const [likes, setLikes] = useState(0);
  const [dislikes, setDislikes] = useState(0);

  return (
    <Card className="mb-4">
      <CardContent className="p-4">
        <div className="flex justify-between items-center">
          <div>
            <h2 className="text-xl font-bold">{post.title}</h2>
            <p className="text-sm text-gray-500">by {post.nickname}</p>
            <p className="text-xs text-gray-400">{post.createdAt}</p>
            {post.updatedAt && (
              <p className="text-xs text-gray-400">(수정됨: {post.updatedAt})</p>
            )}
          </div>
        </div>
        <div
          className="mt-2 text-sm"
          dangerouslySetInnerHTML={{
            __html: expanded ? post.content : post.content.slice(0, 100) + "...",
          }}
        />
        <Button
          variant="link"
          className="text-blue-500 text-sm"
          onClick={() => setExpanded((prev) => !prev)}
        >
          {expanded ? "간략히" : "더보기"}
        </Button>

        <div className="flex space-x-4 mt-2">
          <Button variant="ghost" size="sm" onClick={() => setLikes(likes + 1)}>
            <ThumbsUp className="w-4 h-4" /> {likes}
          </Button>
          <Button variant="ghost" size="sm" onClick={() => setDislikes(dislikes + 1)}>
            <ThumbsDown className="w-4 h-4" /> {dislikes}
          </Button>
          <Button variant="ghost" size="sm" onClick={() => onOpenComments(post)}>
            <MessageSquare className="w-4 h-4" /> 댓글 보기
          </Button>
        </div>
      </CardContent>
    </Card>
  );
};

const CommentModal = ({ post, isOpen, onClose }) => {
  return (
    <Dialog open={isOpen} onOpenChange={onClose}>
      <DialogContent className="w-[400px]">
        <h3 className="text-lg font-bold mb-2">{post?.title} - 댓글</h3>
        <p className="text-sm">여기에 댓글이 표시됩니다.</p>
      </DialogContent>
    </Dialog>
  );
};

const ArticleTest = () => {
  const [posts, setPosts] = useState([]);
  const [page, setPage] = useState(0);
  const [loading, setLoading] = useState(false);
  const [selectedPost, setSelectedPost] = useState(null);
  const loaderRef = useRef();

  const loadMore = async () => {
    setLoading(true);
    const newPosts = await mockFetchPosts(page);
    setPosts((prev) => [...prev, ...newPosts]);
    setPage((prev) => prev + 1);
    setLoading(false);
  };

  useEffect(() => {
    loadMore();
  }, []);

  useEffect(() => {
    const observer = new IntersectionObserver(
      (entries) => {
        if (entries[0].isIntersecting && !loading) {
          loadMore();
        }
      },
      { threshold: 1.0 }
    );
    if (loaderRef.current) {
      observer.observe(loaderRef.current);
    }
    return () => observer.disconnect();
  }, [loaderRef.current, loading]);

  return (
    <div className="max-w-2xl mx-auto py-4">
      {posts.map((post) => (
        <PostCard key={post.id} post={post} onOpenComments={setSelectedPost} />
      ))}
      <div ref={loaderRef} className="h-10 flex justify-center items-center text-gray-500">
        {loading ? "로딩 중..." : "스크롤하여 더 보기"}
      </div>

      <CommentModal
        post={selectedPost}
        isOpen={!!selectedPost}
        onClose={() => setSelectedPost(null)}
      />
    </div>
  );
};

export default ArticleTest;
