import React, { useState } from "react";
import { Editor } from "react-draft-wysiwyg";
import { EditorState, convertToRaw } from "draft-js";
import draftToHtml from "draftjs-to-html";
import "react-draft-wysiwyg/dist/react-draft-wysiwyg.css";

const PostTemp = () => {
  const [editorState, setEditorState] = useState(EditorState.createEmpty());
  const [imageFiles, setImageFiles] = useState([]);

  const onEditorStateChange = (state) => {
    setEditorState(state);
  };

  // 이미지 업로드 콜백 (로컬 미리보기용)
  const uploadImageCallback = (file) => {
    const localUrl = URL.createObjectURL(file);
    setImageFiles((prev) => [...prev, { file, localUrl }]);
    return Promise.resolve({ data: { link: localUrl } });
  };

  const uploadImageToServer = async (file) => {
    const formData = new FormData();
    formData.append("image", file);

    const res = await fetch("http://localhost:5000/upload", {
      method: "POST",
      body: formData,
    });
    if (!res.ok) throw new Error("이미지 업로드 실패");
    const data = await res.json();
    if (!data.url) throw new Error("서버에서 URL을 반환하지 않음");
    return data.url;
  };

  const handleSubmit = async () => {
    try {
      const contentState = editorState.getCurrentContent();
      let rawContent = convertToRaw(contentState);
      const entityMap = rawContent.entityMap;
      const urlMap = {};

      for (let key in entityMap) {
        const entity = entityMap[key];
        if (entity.type === "IMAGE") {
          const localSrc = entity.data.src;
          const imgObj = imageFiles.find((img) => img.localUrl === localSrc);
          if (imgObj) {
            const serverUrl = await uploadImageToServer(imgObj.file);
            urlMap[localSrc] = serverUrl;
            entityMap[key].data.src = serverUrl;
          }
        }
      }

      const htmlContent = draftToHtml(rawContent);
      console.log("업로드된 이미지 URL 맵:", urlMap);
      console.log("작성된 HTML:", htmlContent);

      alert("글 작성이 완료되었습니다!");
      setEditorState(EditorState.createEmpty());
      setImageFiles([]);
    } catch (error) {
      console.error("글 작성 실패:", error);
      alert("글 작성 중 오류가 발생했습니다.");
    }
  };

  const imageBlockRenderer = (contentBlock) => {
    if (contentBlock.getType() === "atomic") {
      return {
        component: MediaComponent,
        editable: false,
      };
    }
    return null;
  };

  const MediaComponent = (props) => {
    const entity = props.contentState.getEntity(props.block.getEntityAt(0));
    const { src, alt, height, width } = entity.getData();
    return (
      <img
        src={src}
        alt={alt || ""}
        style={{ height: height || "auto", width: width || "auto" }}
      />
    );
  };

  // blockType 드롭다운 옵션 스타일링 및 한글 레이블
  const blockTypeOptions = [
    { label: "일반", style: "unstyled", fontSize: "1rem" },
    { label: "제목 1", style: "header-one", fontSize: "2em" },
    { label: "제목 2", style: "header-two", fontSize: "1.5em" },
    { label: "제목 3", style: "header-three", fontSize: "1.17em" },
    { label: "인용문", style: "blockquote", fontSize: "1rem", fontStyle: "italic" },
    { label: "코드", style: "code-block", fontFamily: "monospace", fontSize: "1rem" },
  ];

  return (
    <div style={{ maxWidth: 800, margin: "auto" }}>
      <h2>게시글 작성</h2>
      <Editor
        editorState={editorState}
        onEditorStateChange={onEditorStateChange}
        wrapperClassName="demo-wrapper"
        editorClassName="demo-editor"
        toolbar={{
          options: [
            "inline",
            "blockType",
            "fontSize",
            "list",
            "textAlign",
            "colorPicker",
            "link",
            "embedded",
            "emoji",
            "image",
            "history",
          ],
          inline: {
            options: ["bold", "italic", "underline"],
            bold: { title: "굵게" },
            italic: { title: "기울임" },
            underline: { title: "밑줄" },
          },
          blockType: {
            options: blockTypeOptions.map((opt) => opt.style),
            title: "블록 타입",
            dropdownClassName: "custom-block-dropdown",
            // 커스텀 렌더링 하려면 따로 구현 필요 (아래 설명 참고)
          },
          fontSize: {
            options: [8, 9, 10, 11, 12, 14, 16, 18, 24, 30, 36, 48, 60, 72, 96],
            title: "글자 크기",
          },
          image: {
            uploadCallback: uploadImageCallback,
            previewImage: true,
            alt: { present: true, mandatory: false },
            title: "이미지 업로드",
          },
        }}
        customBlockRenderFunc={imageBlockRenderer}
      />
      {/* blockType 드롭다운 옵션 커스텀 스타일을 위한 CSS 인라인 */}
      <style>
        {`
          /* 드롭다운 내부 옵션 글자 크기 및 스타일 */
          .rdw-dropdown-option {
            display: flex !important;
            align-items: center;
            padding-left: 10px !important;
          }
          .rdw-dropdown-option span {
            flex-grow: 1;
          }
          .rdw-dropdown-option[data-value="header-one"] {
            font-size: 2em !important;
            font-weight: bold;
          }
          .rdw-dropdown-option[data-value="header-two"] {
            font-size: 1.5em !important;
            font-weight: bold;
          }
          .rdw-dropdown-option[data-value="header-three"] {
            font-size: 1.17em !important;
            font-weight: bold;
          }
          .rdw-dropdown-option[data-value="blockquote"] {
            font-style: italic !important;
          }
          .rdw-dropdown-option[data-value="code-block"] {
            font-family: monospace !important;
            background-color: #f5f5f5 !important;
          }
          /* 버튼 툴팁도 한글로 */
          .rdw-option-wrapper[title="Bold"] {
            title: "굵게";
          }
          .rdw-option-wrapper[title="Italic"] {
            title: "기울임";
          }
          .rdw-option-wrapper[title="Underline"] {
            title: "밑줄";
          }
        `}
      </style>
      <button
        style={{
          marginTop: 20,
          padding: "10px 20px",
          backgroundColor: "#4F46E5",
          color: "white",
          border: "none",
          borderRadius: 5,
          cursor: "pointer",
        }}
        onClick={handleSubmit}
      >
        글 업로드
      </button>
    </div>
  );
};

export default PostTemp;
