import React, { useState, useEffect, useCallback } from "react";
import { Editor } from "react-draft-wysiwyg";
import { EditorState, convertToRaw, Modifier } from "draft-js";
import draftToHtml from "draftjs-to-html";
import "react-draft-wysiwyg/dist/react-draft-wysiwyg.css";
import DOMPurify from "dompurify";
import { useSelector } from "react-redux";

import PostRequestWithAccessToken from "../api/axios/requestWithAccessToken";

function generateId() {
  // Í∞ÑÎã®Ìïú Ïú†ÎãàÌÅ¨ ID ÏÉùÏÑ± Ìï®Ïàò (ÎûúÎç§ Î¨∏ÏûêÏó¥)
  return Math.random().toString(36).substring(2, 15);
}

// Ïª§Ïä§ÌÖÄ Ïù¥ÎØ∏ÏßÄ Ïª¥Ìè¨ÎÑåÌä∏: entity dataÏùò idÎ•º Î°úÏª¨ URLÎ°ú Î≥ÄÌôòÌï¥ÏÑú Î†åÎçîÎßÅ
const CustomImage = ({ contentState, entityKey }) => {
  const { id } = contentState.getEntity(entityKey).getData();
  // id ‚Üí Î°úÏª¨ URL Îß§Ìïë
  const localUrl = window.localImageMap?.[id] || null;
  if (!localUrl) return null;
  return <img src={localUrl} alt="uploaded" style={{ maxWidth: "100%" }} />;
};

const PostTemp = () => {
  const [editorState, setEditorState] = useState(EditorState.createEmpty());
  // {id, file, localUrl} Î∞∞Ïó¥ Í¥ÄÎ¶¨
  const [imageFiles, setImageFiles] = useState([]);
  const token = useSelector((state) => state.auth.accessToken);

  // Ï†ÑÏó≠Ïóê Î°úÏª¨ URL Îßµ ÎßåÎì§Í∏∞ (CustomImageÏóêÏÑú Ï†ëÍ∑º Í∞ÄÎä•ÌïòÍ≤å)
  useEffect(() => {
    window.localImageMap = {};
    imageFiles.forEach(({ id, localUrl }) => {
      window.localImageMap[id] = localUrl;
    });
  }, [imageFiles]);

  const onEditorStateChange = (state) => {
    setEditorState(state);
  };

  const uploadImageCallback = (file) => {
    // Ïù¥ÎØ∏ÏßÄÎßàÎã§ Í≥†Ïú† id ÏÉùÏÑ±
    const id = generateId();
    const localUrl = URL.createObjectURL(file);
    setImageFiles((prev) => [...prev, { id, file, localUrl }]);

    // entityMapÏóê idÎßå ÎÑ£ÎèÑÎ°ù Î¶¨ÌÑ¥
    return Promise.resolve({ data: { link: id } });
  };

  // ÏÑúÎ≤Ñ ÏóÖÎ°úÎìú Ìï®Ïàò (Í∏∞Ï°¥ Í∑∏ÎåÄÎ°ú)
  const uploadImageToServer = async (file) => {
    const formData = new FormData();
    formData.append("image", file);

    const res = await fetch("http://localhost:5000/upload", {
      method: "POST",
      body: formData,
    });

    if (!res.ok) throw new Error("Ïù¥ÎØ∏ÏßÄ ÏóÖÎ°úÎìú Ïã§Ìå®");

    const data = await res.json();
    if (!data.url) throw new Error("ÏÑúÎ≤ÑÏóêÏÑú URLÏùÑ Î∞õÏßÄ Î™ªÌï®");

    return data.url;
  };

  const sanitizeHtmlWithImageFilter = (dirtyHtml) => {
    // ÌïÑÏöîÏãú ÌïÑÌÑ∞ÎßÅ Íµ¨ÌòÑ (ÏÉùÎûµÌï¥ÎèÑ Îê®)
    return DOMPurify.sanitize(dirtyHtml);
  };

  const handleSubmit = async () => {
    try {
      const contentState = editorState.getCurrentContent();
      let rawContent = convertToRaw(contentState);
      const entityMap = rawContent.entityMap;

      // id Í∏∞Ï§ÄÏúºÎ°ú ÏÑúÎ≤Ñ ÏóÖÎ°úÎìú ÌõÑ URL ÍµêÏ≤¥
      for (const key in entityMap) {
        const entity = entityMap[key];
        if (entity.type === "IMAGE") {
          const id = entity.data.link; // idÎ•º ÎßÅÌÅ¨Î°ú ÎÑ£ÏóàÏúºÎØÄÎ°ú
          const imgObj = imageFiles.find((img) => img.id === id);
          if (imgObj) {
            const serverUrl = await uploadImageToServer(imgObj.file);
            entityMap[key].data.link = serverUrl;
          }
        }
      }

      const htmlContent = draftToHtml(rawContent);
      const cleanHtml = sanitizeHtmlWithImageFilter(htmlContent);

      console.log("HTML:", cleanHtml);

      const titleInput = document.getElementById("draft-title");
      const res = await PostRequestWithAccessToken(token, "/post", {
        title: titleInput?.value || "",
        content: cleanHtml,
      });

      console.log("ÏûëÏÑ± ÏôÑÎ£å:", res.data);
      alert("Í∏Ä ÏûëÏÑ±Ïù¥ ÏôÑÎ£åÎêòÏóàÏäµÎãàÎã§!");

      setEditorState(EditorState.createEmpty());
      setImageFiles([]);
      if (titleInput) titleInput.value = "";
    } catch (error) {
      console.error("Í∏Ä ÏûëÏÑ± Ïã§Ìå®:", error);
      alert("Í∏Ä ÏûëÏÑ± Ï§ë Ïò§Î•òÍ∞Ä Î∞úÏÉùÌñàÏäµÎãàÎã§.");
    }
  };

  // entity ÌÉÄÏûÖÎ≥Ñ Î†åÎçîÎü¨ (Ïù¥ÎØ∏ÏßÄÎäî Ïª§Ïä§ÌÖÄ Ïª¥Ìè¨ÎÑåÌä∏Î°ú Î†åÎçîÎßÅ)
  const entityRenderer = {
    IMAGE: (props) => <CustomImage {...props} />,
  };

  return (
    <div className="flex flex-col w-full">
      {/* Ï†úÎ™© ÏûÖÎ†•Ï∞Ω */}
      <input
        id="draft-title"
        placeholder="Ï†úÎ™©ÏùÑ ÏûÖÎ†•ÌïòÏÑ∏Ïöî"
        className="mb-3 p-2 border border-gray-300 rounded focus:border-indigo-300"
      />

      {/* ÏóêÎîîÌÑ∞ */}
      <div className="border rounded-md mb-4 h-[590px] flex flex-col">
        <Editor
          editorState={editorState}
          onEditorStateChange={onEditorStateChange}
          wrapperClassName="flex flex-col h-full"
          toolbarClassName="!sticky top-0 z-10 bg-white mb-2"
          editorClassName="flex-1 overflow-y-auto px-2"
          toolbar={{
            options: [
              "inline",
              "colorPicker",
              "blockType",
              "list",
              "textAlign",
              "emoji",
              "image",
              "remove",
              "history",
              "fontFamily",
              "fontSize",
            ],
            inline: {
              options: ["bold", "italic", "underline", "strikethrough"],
              bold: { title: "ÍµµÍ≤å" },
              italic: { title: "Í∏∞Ïö∏Ïù¥Í∏∞" },
              underline: { title: "Î∞ëÏ§Ñ" },
              strikethrough: { title: "Ï∑®ÏÜåÏÑ†" },
            },
            blockType: {
              options: ["Normal", "Blockquote", "Code"],
            },
            fontSize: {
              options: [8, 9, 10, 11, 12, 14, 16, 18, 24, 30, 36, 48, 60, 72, 96],
            },
            fontFamily: {
              options: [
                "Arial",
                "Georgia",
                "Impact",
                "Tahoma",
                "Times New Roman",
                "Verdana",
              ],
            },
            list: {
              options: ["unordered", "ordered"],
            },
            textAlign: {
              options: ["left", "center", "right", "justify"],
            },
            colorPicker: {},
            link: {
              options: ["link", "unlink"],
            },
            emoji: {},
            image: {
              uploadCallback: uploadImageCallback,
              previewImage: true,
              alt: { present: true, mandatory: false },
              title: "Ïù¥ÎØ∏ÏßÄ ÏóÖÎ°úÎìú",
              uploadEnabled: true,
              urlEnabled: false,
            },
            remove: {},
            history: {
              options: ["undo", "redo"],
            },
          }}
          // entity Ïª§Ïä§ÌÖÄ Î†åÎçîÎü¨ Ïó∞Í≤∞
          customBlockRenderFunc={(block) => {
            if (block.getType() === "atomic") {
              const contentState = editorState.getCurrentContent();
              const entityKey = block.getEntityAt(0);
              if (!entityKey) return null;
              const entity = contentState.getEntity(entityKey);
              if (entity.getType() === "IMAGE") {
                return {
                  component: () => (
                    <CustomImage contentState={contentState} entityKey={entityKey} />
                  ),
                  editable: false,
                };
              }
            }
            return null;
          }}
        />
      </div>

      {/* Î≤ÑÌäº */}
      <div className="mt-auto flex justify-center">
        <button
          className="
            px-2
            py-2
            bg-indigo-500
            text-xl
            rounded-full
            cursor-pointer
            hover:bg-indigo-700
          "
          onClick={handleSubmit}
        >
          ü™∂
        </button>
      </div>
    </div>
  );
};

export default PostTemp;
