import React, { useState, useEffect, useRef, useCallback } from 'react';
import PostCard from './PostCard';
import { useDispatch, useSelector } from 'react-redux';
import { showModal } from '../redux/modalStackSlice';

const API_URL = 'http://localhost:8080/dumbo-backend/post';
const MAX_POSTS = 40;

export default function ArticleTest() {
  const [posts, setPosts] = useState([]);
  const [topCursor, setTopCursor] = useState(null); // Í∞ÄÏû• ÏúÑ Í≤åÏãúÍ∏Ä Ïª§ÏÑú
  const [bottomCursor, setBottomCursor] = useState(null); // Í∞ÄÏû• ÏïÑÎûò Í≤åÏãúÍ∏Ä Ïª§ÏÑú
  const [hasMoreTop, setHasMoreTop] = useState(true);
  const [hasMoreBottom, setHasMoreBottom] = useState(true);
  const [isLoading, setIsLoading] = useState(false);

  const topRef = useRef(null);
  const bottomRef = useRef(null);
  const containerRef = useRef(null);

  const dispatch = useDispatch();

  const fetchPosts = useCallback(async (direction) => {
    if (isLoading) return;

    const isTop = direction === 'up';
    if ((isTop && !hasMoreTop) || (!isTop && !hasMoreBottom)) return;

    setIsLoading(true);

    try {
      const params = new URLSearchParams();
      const cursor = isTop ? topCursor : bottomCursor;
      if (cursor?.createdAt) params.append('createdAtCursor', cursor.createdAt);
      if (cursor?.postId) params.append('postIdCursor', cursor.postId);
      if (isTop) params.append('reverse', 'true');

      const res = await fetch(`${API_URL}?${params.toString()}`);
      if (!res.ok) throw new Error('Failed to fetch posts');

      const json = await res.json();
      const newPosts = json.data || [];

      setPosts((prev) => {
        const merged = isTop ? [...newPosts, ...prev] : [...prev, ...newPosts];
        return merged.slice(Math.max(merged.length - MAX_POSTS, 0));
      });

      if (isTop) {
        setHasMoreTop(json.hasMore);
        if (newPosts.length > 0) {
          const first = newPosts[0];
          setTopCursor({ createdAt: first.created_at, postId: first.post_id });
        }
      } else {
        setHasMoreBottom(json.hasMore);
        if (newPosts.length > 0) {
          const last = newPosts[newPosts.length - 1];
          setBottomCursor({ createdAt: last.created_at, postId: last.post_id });
        }
      }
    } catch (error) {
      console.error('Í≤åÏãúÍ∏Ä Î°úÎî© Ïò§Î•ò:', error);
    } finally {
      setIsLoading(false);
    }
  }, [topCursor, bottomCursor, hasMoreTop, hasMoreBottom, isLoading]);

  useEffect(() => {
    fetchPosts('down'); // Ï¥àÍ∏∞ Î°úÎî© (ÏïÑÎûòÎ∞©Ìñ•)
  }, []);

  useEffect(() => {
    const topObserver = new IntersectionObserver(
      ([entry]) => {
        if (entry.isIntersecting) fetchPosts('up');
      },
      { root: null, rootMargin: '100px', threshold: 0.01 }
    );

    const bottomObserver = new IntersectionObserver(
      ([entry]) => {
        if (entry.isIntersecting) fetchPosts('down');
      },
      { root: null, rootMargin: '200px', threshold: 0.01 }
    );

    if (topRef.current) topObserver.observe(topRef.current);
    if (bottomRef.current) bottomObserver.observe(bottomRef.current);

    return () => {
      if (topRef.current) topObserver.unobserve(topRef.current);
      if (bottomRef.current) bottomObserver.unobserve(bottomRef.current);
    };
  }, [fetchPosts]);

  const modalStack = useSelector((state) => state.modal.modalStack);
  useEffect(() => {
    document.body.style.overflow = modalStack.length > 0 ? 'hidden' : '';
    return () => {
      document.body.style.overflow = '';
    };
  }, [modalStack.length]);

  const isLoggedIn = useSelector((state) => state.auth.accessToken);

  return (
    <div ref={containerRef} className="max-w-3xl mx-auto mt-10 px-4">
      <div ref={topRef} style={{ height: 1 }} />
      {posts.map((post) => (
        <PostCard key={post.post_id} post={post} />
      ))}
      <div ref={bottomRef} style={{ height: 1 }} />

      {isLoading && (
        <div className="flex justify-center py-6">
          <div className="animate-spin rounded-full h-8 w-8 border-t-2 border-b-2 border-indigo-500" />
        </div>
      )}

      {!hasMoreTop && !hasMoreBottom && (
        <div className="text-center text-gray-400 py-4">
          Î™®Îì† Í≤åÏãúÍ∏ÄÏùÑ Î∂àÎü¨ÏôîÏäµÎãàÎã§.
        </div>
      )}

      {isLoggedIn && (
        <button
          onClick={() => dispatch(showModal('draft'))}
          className="fixed left-8 bottom-8 px-4 py-4 bg-indigo-500 text-2xl rounded-full shadow-lg cursor-pointer hover:bg-indigo-700"
        >
          ü™∂
        </button>
      )}
    </div>
  );
}
