import { useState } from 'react';

export default function LoginModal() {
  const [isOpen, setIsOpen] = useState(false);

  function toggleIsOpen() {
    setIsOpen(!isOpen);
  }

  return (
    <>
      <button
        className="px-4 py-2 bg-blue-600 text-white rounded hover:bg-blue-700"
        onClick={() => setIsOpen(true)}
      >
        모달 열기
      </button>

      {isOpen && (
        // 전체화면 배경 (반투명 검정)
        <div
          className="fixed inset-0 bg-black bg-opacity-50 flex justify-center items-center z-50"
          onClick={toggleIsOpen} // 배경 클릭 시 닫기
        >
          {/* 모달 박스 */}
          <div
            className="bg-white rounded-lg p-6 w-96 max-h-[80vh] overflow-y-auto"
            onClick={(e) => e.stopPropagation()} // 모달 내부 클릭 시 배경 클릭 이벤트 차단
          >
            <button
              className="mb-4 px-3 py-1 bg-gray-300 rounded hover:bg-gray-400"
              onClick={toggleIsOpen}
            >
              모달 닫기
            </button>

            <h2 className="text-xl font-semibold mb-4">로그인</h2>
            <p>여기에 로그인 폼이나 게시글 등 원하는 내용을 넣으세요.</p>
          </div>
        </div>
      )}
    </>
  );
}
